INSERT INTO `site_info` VALUES ('90', '<h1>\n <!--<$[TITLE]>start-->重庆市环保局2012年上半年面向基层公开遴选公务员拟遴选人员公示\n <!--<$[TITLE]>end--></h1><div id=\"Zoom\"> \n <!--<$[CONTENT]>start--> \n <p> <style>\r\n/* Generator: eWebEditor */\r\np.MsoNormal, li.MsoNormal, div.MsoNormal {margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\"Times New Roman\";}\r\np.ParaCharCharCharCharCharCharCharCharCharCharCharChar, li.ParaCharCharCharCharCharCharCharCharCharCharCharChar, div.ParaCharCharCharCharCharCharCharCharCharCharCharChar {margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;tab-stops:right -106.0pt;layout-grid-mode:char;font-size:12.0pt;font-family:Tahoma;letter-spacing:.3pt;}\r\ndiv.Section1 {page:Section1;}\r\n</style> </p>\n <p style=\"LINE-HEIGHT: 150%; TEXT-INDENT: 21pt; MARGIN-LEFT: 9.05pt\" class=\"MsoNormal\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; COLOR: black; FONT-SIZE: 14pt\">按照《中共重庆市委组织部 重庆市人力资源和社会保障局重庆市公务员局<span>2012</span>年上半年重庆市市级机关面向基层公开遴选公务员公告》要求，现将拟遴选人员予以公示。</span></p> \n <p style=\"LINE-HEIGHT: 150%\" class=\"MsoNormal\"><b><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; COLOR: black; FONT-SIZE: 14pt\">一、公示期</span></b></p> \n <p style=\"LINE-HEIGHT: 150%; TEXT-INDENT: 28pt\" class=\"MsoNormal\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; COLOR: black; FONT-SIZE: 14pt\">2012</span><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; COLOR: black; FONT-SIZE: 14pt\">年<span>7</span>月<span>25</span>日</span><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; COLOR: black; FONT-SIZE: 14pt\">至<span>7</span>月<span>31</span>日。</span></p> \n <p style=\"LINE-HEIGHT: 150%\" class=\"MsoNormal\"><b><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; COLOR: black; FONT-SIZE: 14pt\">二、受理地点及电话</span></b></p> \n <p style=\"LINE-HEIGHT: 150%; TEXT-INDENT: 28pt\" class=\"MsoNormal\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; COLOR: black; FONT-SIZE: 14pt\">地 点：重庆市环保局组织人事处</span><