INSERT INTO `site_info` VALUES ('93', '<h1>\n <!--<$[TITLE]>start-->重庆市辐射环境监督管理站关于2012年上半年公开遴选公务员加试《职位业务水平测试》有关情况的通知\n <!--<$[TITLE]>end--></h1><div id=\"Zoom\"> \n <!--<$[CONTENT]>start--> \n <p><style type=\"text/css\">/* Generator: eWebEditor */\np.MsoNormal, li.MsoNormal, div.MsoNormal {margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\"Times New Roman\";}\np {margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;}\ndiv.Section1 {page:Section1;}\n</style> </p>\n <p align=\"center\" class=\"MsoNormal\" style=\"TEXT-ALIGN: center; LINE-HEIGHT: 150%\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 黑体; FONT-SIZE: 18pt\">重庆市辐射环境监督管理站关于<span>2012</span>年上半年公开遴选</span></p> \n <p align=\"center\" class=\"MsoNormal\" style=\"TEXT-ALIGN: center; LINE-HEIGHT: 150%\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 黑体; FONT-SIZE: 18pt\">公务员加试《职位业务水平测试》有关情况的通知</span></p> \n <p class=\"MsoNormal\">&nbsp;</p> \n <p class=\"MsoNormal\" style=\"LINE-HEIGHT: 150%\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\">各位考生：</span></p> \n <p class=\"MsoNormal\" style=\"LINE-HEIGHT: 150%; TEXT-INDENT: 21pt\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\"><span>&nbsp; </span></span><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\">现将重庆市辐射环境监督管理站参加<span>2012</span>年上半年市级机关公开遴选加试《职位业务水平测试》有关事项通知如下：</span></p> \n <p align=\"left\" class=\"MsoNormal\" style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 32.2pt\"><b><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\">一、考试时间</span></b></p> \n <p align=\"left\" class=\"MsoNormal\" style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 25.35pt\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\">《职位业务水平测试》时间定于<span>2012</span>年<span>4</span>月<span>22</span>日<span>14</span>：<span>30</span>至<span>16</span>：<span>30</span>。</span></p> \n <p align=\"left\" class=\"MsoNormal\" style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 39.05pt\"><b><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\">二、考试地点</span></b></p> \n <p align=\"left\" class=\"MsoNormal\" style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 35pt\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\">重庆市武警招待所<span>7</span>楼会议室</span></p> \n <p style=\"LINE-HEIGHT: 150%; TEXT-INDENT: 35pt\"><span style=\"LINE-HEIGHT: 150%; FONT-SIZE: 14pt\">地址：渝北区龙溪街道龙山路<span>66</span>号武警重庆总队旁</span></p> \n <p style=\"LINE-HEIGHT: 150%; TEXT-INDENT: 35pt\"><span style=\"LINE-HEIGHT: 150%; FONT-SIZE: 14pt\">联系电话：<span>67905959</span>（总机）、<span>67606122</span>（总台）</span></p> \n <p align=\"left\" class=\"MsoNormal\" style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 39.35pt\"><b><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\">三、《职位业务水平测试》复习参考范围</span></b></p> \n <p align=\"left\" class=\"MsoNormal\" style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 39.2pt\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\">岗位：电磁辐射监督管理及监测</span></p> \n <p align=\"left\" class=\"MsoNormal\" style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 21pt\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\"><span>&nbsp;</span><span>&nbsp; </span>1</span><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\">、电磁环境基础知识；</span></p> \n <p align=\"left\" class=\"MsoNormal\" style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 25.2pt; MARGIN-LEFT: 15.95pt\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\">2</span><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\">、《中华人民共和国环境保护法》、《重庆市环境保护条例》等环境保护法律法规；</span></p> \n <p align=\"left\" class=\"MsoNormal\" style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 39.2pt\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\">3</span><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\">、《电磁辐射环境保护管理办法》；</span></p> \n <p align=\"left\" class=\"MsoNormal\" style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 39.2pt\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\">4</span><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\">、电磁辐射环境标准及监测技术规范等。</span></p> \n <p align=\"left\" class=\"MsoNormal\" style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 25.3pt\"><b><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\">四、准考证发放</span></b></p> \n <p align=\"left\" class=\"MsoNormal\" style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 25.2pt\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\">考生参加《职位业务水平测试》时，请携带身份证原件和市公务员局《综合知识测试》准考证。《职位业务水平测试》准考证将继续采用《综合知识测试》发放的准考证，请于<span>4</span>月<span>22</span>日<span>14</span>时前在考试地点统一编写新的准考证号并加盖公章。</span></p> \n <p align=\"left\" class=\"MsoNormal\" style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 25.2pt\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\">联系人：王<span><span>&nbsp; </span></span>玲</span></p> \n <p align=\"left\" class=\"MsoNormal\" style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 25.2pt\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\">联系电话：<span>89031703</span></span></p> \n <p align=\"left\" class=\"MsoNormal\" style=\"TEXT-ALIGN: left; LINE-HEIGHT: 150%; TEXT-INDENT: 25.2pt\">&nbsp;</p> \n <p align=\"right\" class=\"MsoNormal\" style=\"TEXT-ALIGN: right; LINE-HEIGHT: 150%; TEXT-INDENT: 25.2pt\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\">二</span><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\">〇</span><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 14pt\">一二年四月十六日</span></p> \n <p></p> \n <!--endprint--> \n <!--<$[CONTENT]>end--> \n</div>', '重庆市辐射环境监督管理站关于2012年上半年公开遴选公务员加试《职位业务水平测试》有关情况的通知', '2012-04-16 00:00:00', 'http://www.cepb.gov.cn/doc/2012/04/16/30077.shtml');
INSERT INTO `site_info` VALUES ('94', '<h1>\n <!--<$[TITLE]>start-->关于调整我市环境监测服务费收费标准的通知\n <!--<$[TITLE]>end--></h1><div id=\"Zoom\"> \n <!--<$[CONTENT]>start--> \n <p></p>\n <div align=\"center\"> \n  <h1><font size=\"3\">渝价[2002]771号文</font></h1> \n </div> \n <div id=\"BodyLabel\"> \n  <div align=\"center\">\n   <b>重庆市物价局&nbsp; 重庆市财政局</b>\n  </div> \n  <div align=\"center\">\n   <b>关于调整我市环境监测服务费收费标准的通知</b>\n  </div> \n  <div>\n   市环保局：\n  </div> \n  <div>\n   &nbsp;&nbsp;&nbsp; 根据原国家物价局、财政部《关于发布环保系统行政事业性收费项目及标准的通知》（[1992]价费字178号）等有关规定，考虑到我市物价上涨水平，环境监测服务的合理耗费，以及经济发展水平和企业承受能力等情况，经市政府批准，决定对我市环境监测服务费收费标准进行调整。现将调整后的《重庆市环境监测收费管理办法》（见附件）印发给你们，请遵照执行。\n  </div> \n  <div>\n   本通知请于2003年1月1日起执行。原有规定凡与本通知不符的按本通知执行。\n  </div> \n  <div align=\"left\">\n   附件：\n  </div> \n  <div>\n   &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;重庆市环境监测收费管理办法&nbsp;\n  </div> \n  <div>\n   一、凡在我市范围内，持有《重庆市环境监测资质审查合格证》和《计量认证合格证》的环境监测机构，在完成上级下达的环境监测任务的前提下，受单位或个人委托，在以下服务范围内开展对外技术服务，按本办法收取费用：\n  </div> \n  <div>\n   1、各项环境背景值调查和环境评价；\n  </div> \n  <div>\n   2、对外单位委托的样品分析，固定和流动污染源监测以及污染纠纷、事故仲裁监测；\n  </div> \n  <div>\n   3、治理工程项目的研究设计及其环境效益分析评价；\n  </div> \n  <div>\n   4、受有关单位委托承担环境保护方面的技术咨询、技术服务、成果转让和其它方面的项目；\n  </div> \n  <div>\n   5、工业污染源成果综合服务项目，包括污染源档案、各类技术和研究报告、图集、数据库等。\n  </div> \n  <div>\n   二、收费项目及标准（见附表）\n  </div> \n  <div>\n   三、接受委托的环境监测机构，应与委托单位和个人签订合同，合同内容包括服务项目、收费项目及收费标准，明确双方的权利、义务和违约责任。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n  </div> \n  <div align=\"center\">\n   <b>收费项目及标准</b>\n  </div> \n  <div align=\"center\">\n   <b>（一）采样工作收费标准</b>\n  </div> \n  <div align=\"center\"> \n   <table border=\"1\" cellpadding=\"0\"> \n    <tbody> \n     <tr> \n      <td width=\"8%\"> \n       <div align=\"center\">\n        序号\n       </div> </td> \n      <td colspan=\"2\" width=\"33%\"> \n       <div align=\"center\">\n        项目\n       </div> </td> \n      <td width=\"24%\"> \n       <div align=\"center\">\n        计价单位\n       </div> </td> \n      <td width=\"34%\"> \n       <div align=\"center\">\n        收费标准（元）\n       </div> </td> \n     </tr> \n     <tr> \n      <td rowspan=\"2\" width=\"8%\"> \n       <div align=\"center\">\n        1\n       </div> </td> \n      <td rowspan=\"2\" width=\"11%\"> \n       <div align=\"center\">\n        大气\n       </div> </td> \n      <td width=\"23%\"> \n       <div align=\"left\">\n        每小时采样\n       </div> </td> \n      <td width=\"24%\"> \n       <div align=\"center\">\n        一个样/点\n       </div> </td> \n      <td width=\"34%\"> \n       <div align=\"center\">\n        白天&nbsp;&nbsp;&nbsp;10夜间&nbsp;&nbsp;&nbsp;16\n       </div> </td> \n     </tr> \n     <tr> \n      <td width=\"23%\"> \n       <div align=\"left\">\n        24小时连续采样\n       </div> </td> \n      <td width=\"24%\"> \n       <div align=\"center\">\n        一个样/点\n       </div> </td> \n      <td width=\"34%\"> \n       <div align=\"center\">\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;160\n       </div> </td> \n     </tr> \n     <tr> \n      <td rowspan=\"2\" width=\"8%\"> \n       <div align=\"center\">\n        2\n       </div> </td> \n      <td rowspan=\"2\" width=\"11%\"> \n       <div align=\"center\">\n        水质\n       </div> </td> \n      <td width=\"23%\"> \n       <div align=\"left\">\n        地面水、井水\n       </div> </td> \n      <td width=\"24%\"> \n       <div align=\"center\">\n        一个样/点\n       </div> </td> \n      <td width=\"34%\"> \n       <div align=\"center\">\n        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;6\n       </div> </td> \n     </tr> \n     <tr> \n      <td width=\"23%\"> \n       <div align=\"left\">\n        污水\n       </div> </td> \n      <td width=\"24%\"> \n       <div align=\"center\">\n        一个样/点\n       </div> </td> \n      <td width=\"34%\"> \n       <div 