INSERT INTO `site_info` VALUES ('95', '<h1>\n <!--<$[TITLE]>start-->环境监测数据弄虚作假行为判定及处理办法\n <!--<$[TITLE]>end--></h1><div id=\"Zoom\"> \n <!--<$[CONTENT]>start--> \n <p></p>\n <p align=\"center\" style=\"text-align: center; layout-grid-mode: char\">&nbsp;</p> \n <p align=\"center\" style=\"text-align: center; line-height: 170%; layout-grid-mode: char\">&nbsp;</p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">第一条　为保障环境监测数据真实准确，依法查处环境监测数据弄虚作假行为，依据《环境保护法》和《生态监测网络建设方案》（国办发〔2015〕56号）等有关法律法规和文件，结合工作实际，制定本办法。</span></span></p> \n <h4 style=\"page-break-after: auto; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-weight: normal\">第二条</span><span style=\"font-weight: normal\">　本办法所称环境监测数据弄虚作假行为，系指故意违反国家法律法规、规章等以及环境监测技术规范，篡改、伪造或者指使篡改、伪造环境监测数据等行为。</span></span></span></h4> \n <h4 style=\"page-break-after: auto; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-weight: normal\">本办法所称环境监测数据，系指按照相关技术规范和规定，通过手工或者自动监测方式取得的环境监测原始记录、分析数据、监测报告等信息。</span></span></span></h4> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"color: black\">本办法所称环境监测机构，系指县级以上环境保护主管部门所属环境监测机构、其他负有环境保护监督管理职责的部门所属环境监测机构以及承担环境监测工作的实验室与从事环境监测业务的企事业单位等其他社会环境监测机构。</span></span></span></p> \n <h4 style=\"page-break-after: auto; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-weight: normal\">第三条</span><span style=\"font-weight: normal\">　本办法适用于以下活动中涉及的环境监测数据弄虚作假行为：</span></span></span></h4> \n <h4 style=\"page-break-after: auto; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-weight: normal\">（一）依法开展的环境质量监测、污染源监测、应急监测；</span></span></span></h4> \n <h4 style=\"page-break-after: auto; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-weight: normal\">（二）监管执法涉及的环境监测；</span></span></span></h4> \n <h4 style=\"page-break-after: auto; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-weight: normal\">（三）政府购买的环境监测服务或者</span><span style=\"font-weight: normal\">委托开展的环境监测；</span></span></span></h4> \n <h4 style=\"page-break-after: auto; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-weight: normal\">（四）企事业单位依法开展或者委托开展的自行监测；</span></span></span></h4> \n <h4 style=\"page-break-after: auto; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-weight: normal\">（五）依照法律、法规开展的其他环境监测行为。</span></span></span></h4> \n <h4 style=\"page-break-after: auto; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-weight: normal\">第四条</span><span style=\"font-weight: normal\">　篡改监测数据，系指利用某种职务或者工作上的便利条件，故意干预环境监测活动的正常开展，导致监测数据失真</span><span style=\"font-weight: normal\"><span style=\"color: black\">的行为，包括以下情形：</span></span></span></span></h4> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（一）未经批准部门同意，擅自<span style=\"color: black\">停运、</span>变更、增减环境监测点位或者故意改变环境监测点位属性的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（二）采取人工遮挡、堵塞和喷淋等方式，干扰采样口或周围局部环境的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（三）人为操纵、干预或者破坏排污单位生产工况、污染源净化设施，使生产或污染状况不符合实际情况的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（四）稀释排放或者旁路排放，或者将部分或全部污染物不经规范的排污口排放，逃避自动监控设施监控的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（五）破坏、损毁监测设备站房、通讯线路、信息采集传输设备、视频设备、电力设备、空调、风机、采样泵、采样管线、监控仪器或仪表以及其他监测监控或辅助设施的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（六）故意更换、隐匿、遗弃监测样品或者通过稀释、吸附、吸收、过滤、改变样品保存条件等方式改变监测样品性质的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（七）故意漏检关键项目或者无正当理由故意改动关键项目的监测方法的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（八）故意改动、干扰仪器设备的环境条件或运行状态或者删除、修改、增加、干扰监测设备中存储、处理、传输的数据和应用程序，或者人为使用试剂、标样干扰仪器的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（九）未向环境保护主管部门备案，自动监测设备暗藏可通过特殊代码、组合按键、远程登录、遥控、模拟等方式进入不公开的操作界面对自动监测设备的参数和监测数据进行秘密修改的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（十）故意不真实记录或者选择性记录原始数据的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（十一）篡改、销毁原始记录，或者不按规范传输原始数据的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（十二）对原始数据进行不合理修约、取舍，或者有选择性评价监测数据、出具监测报告或者发布结果，以至评价结论失真的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（十三）擅自修改数据的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（十四）其他涉嫌篡改监测数据的情形。</span></span></p> \n <h4 style=\"page-break-after: auto; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-weight: normal\">第五条</span><span style=\"font-weight: normal\">　伪造监测数据，系指没有实施实质性的环境监测活动，凭空编造虚假监测数据的行为，包括以下情形：</span></span></span></h4> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（一）纸质原始记录与电子存储记录不一致，或者谱图与分析结果不对应，或者用其他样品的分析结果和图谱替代的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（二）监测报告与原始记录信息不一致，或者没有相应原始数据的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（三）监测报告的副本与正本不一致的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（四）伪造监测时间或者签名的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（五）通过仪器数据模拟功能，或者植入模拟软件，凭空生成监测数据的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（六）未开展采样、分析，直接出具监测数据或者到现场采样、但未开设烟道采样口，出具监测报告的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（七）未按规定对样品留样或保存,导致无法对监测结果进行复核的;</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（八）其他涉嫌伪造监测数据的情形。</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">第六条　涉嫌指使篡改、伪造监测数据的行为，包括以下情形： </span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（一）强令、授意有关人员篡改、伪造监测数据的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（二）将考核达标或者评比排名情况列为下属监测机构、监测人员的工作考核要求，意图干预监测数据的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（三）无正当理由，强制要求监测机构多次监测并从中挑选数据，或者无正当理由拒签上报监测数据的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（四）委托方人员授意监测机构工作人员篡改、伪造监测数据或者在未作整改的前提下，进行多家或多次监测委托，挑选其中“合格”监测报告的；</span></span></p> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">（五）其他涉嫌指使篡改、伪造监测数据的情形。</span></span></p> \n <h4 style=\"page-break-after: auto; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-weight: normal\">第七条</span>　<span style=\"font-weight: normal\">环境监测机构及其负责人对监测数据的真实性和准确性负责。</span></span></span></h4> \n <h4 style=\"page-break-after: auto; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-weight: normal\">负责环境自动监测设备日常运行维护的机构及其负责人按照运行维护合同对监测数据承担责任。</span></span></span></h4> \n <h4 style=\"page-break-after: auto; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-weight: normal\">第八条</span><span style=\"font-weight: normal\">　<span style=\"letter-spacing: -0.2pt\">地市级以上人民政府环境保护主管部门负责调查环境监测数据弄虚作假行为。</span></span><span style=\"letter-spacing: -0.2pt; font-weight: normal\"><span style=\"color: black\">地市级以上人民政府</span></span><span style=\"letter-spacing: -0.2pt; font-weight: normal\">环境保护主管部门应定期或者不定期组织开展环境监测质量监督检查，发现环境监测数据弄虚作假行为的，应当依法查处，并向上级环境保护主管部门报告。</span></span></span></h4> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">第九条　对干预环境监测活动，指使篡改、伪造监测数据的行为，相关人员应如实记录。任何单位和个人有权举报环境监测数据弄虚作假行为，接受举报的环境保护主管部门应当为举报人保密，对能提供基本事实线索或相关证明材料的举报，应当予以受理。</span></span></p> \n <h4 style=\"page-break-after: auto; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-weight: normal\">第十条</span>　<span style=\"font-weight: normal\">负责调查的</span><span style=\"font-weight: normal\"><span style=\"color: black\">环境保护主管部门应当通报环境监测数据弄虚作假行为及相关责任人，</span></span><span style=\"font-weight: normal\"><span style=\"color: black\">记入社会诚信档案，及时向社会公布</span></span><span style=\"font-weight: normal\"><span style=\"color: black\">。</span></span></span></span></h4> \n <p style=\"line-height: 155%; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\">第十一条<strong>　</strong>环境保护主管部门发现篡改、伪造监测数据，涉及目标考核的，视情节严重程度将考核结果降低等级或者确定为不合格，情节严重的，取消授予的环境保护荣誉称号；涉及县域生态考核的，视情节严重程度，建议国务院财政主管部门减少或者取消当年中央财政资金转移支付；涉及《大气污染防治行动计划》《水污染防治行动计划》排名的，分别以当日或当月监测数据的历史最高浓度值计算排名。</span></span></p> \n <h4 style=\"page-break-after: auto; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-weight: normal\">第十二条</span><span style=\"font-weight: normal\"><span style=\"color: black\">　社会环境监测机构以及从事环境监测设备维护、运营的机构篡改、伪造监测数据或出具虚假监测报告的，由负责调查的环境保护主管部门将该机构和涉及弄虚作假行为的人员列入不良记录名单，并报上级环境保护主管部门，禁止其参与政府购买环境监测服务或政府委托项目。</span></span></span></span></h4> \n <h4 style=\"page-break-after: auto; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-weight: normal\">第十三条</span><span style=\"font-weight: normal\"><span style=\"color: black\">　监测仪器设备应当具备防止修改、伪造监测数据的功能，监测仪器设备生产及销售单位配合环境监测数据造假的，由负责调查的环境保护部主管部门通报公示生产厂家、销售单位及其产品名录，并上报环境保护部，将涉嫌弄虚作假的单位列入不良记录名单，禁止其参与政府购买环境监测服务或政府委托项目，对安装在企业的设备不予验收、联网。</span></span></span></span></h4> \n <h4 style=\"page-break-after: auto; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-weight: normal\">第十四条</span><span style=\"font-weight: normal\"><span style=\"color: black\">　国家机关工作人员篡改、伪造或指使篡改、伪造监测数据的，由负责调查的环境保护主管部门提出建议，移送有关任免机关或监察机关依据《行政机关公务员处分条例》和《事业单位工作人员处分暂行规定》的有关规定予以处理。</span></span></span></span></h4> \n <h4 style=\"page-break-after: auto; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-weight: normal\">第十五条</span><span style=\"color: black\">　</span><span style=\"font-weight: normal\"><span style=\"color: black\">党政领导干部指使篡改、伪造监测数据的，由负责调查的环境保护主管部门提出建议，移送有关任免机关或监察机关依据《党政领导干部生态环境损害责任追究办法（试行）》的有关规定予以处理。</span></span></span></span></h4> \n <h4 style=\"page-break-after: auto; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-weight: normal\">第十六条</span><span style=\"font-weight: normal\"><span style=\"color: black\">　环境监测数据弄虚作假行为构成违法的，按照有关法律法规的规定处理。</span></span></span></span></h4> \n <h4 style=\"page-break-after: auto; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-weight: normal\">第十七条</span><span style=\"font-weight: normal\"><span style=\"color: black\">　本办法由国务院环境保护主管部门负责解释。</span></span></span></span></h4> \n <h4 style=\"page-break-after: auto; text-indent: 30pt; layout-grid-mode: char\"><span style=\"font-size: 20px\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-weight: normal\">第十八条</span><span style=\"font-weight: normal\"><span style=\"color: black\">　本办法自2016年1月1日起实施。</span></span></span></span></h4> \n <p></p> \n <!--endprint--> \n <!--<$[CONTENT]>end--> \n</div>', '环境监测数据弄虚作假行为判定及处理办法', '2017-03-13 00:00:00', 'http://www.cepb.gov.cn/doc/2017/03/13/155423.shtml');
INSERT INTO `site_info` VALUES ('96', '<h1>\n <!--<$[TITLE]>start-->关于调整我市环境监测服务费收费标准的通知\n <!--<$[TITLE]>end--></h1><div id=\"Zoom\"> \n <!--<$[CONTENT]>start--> \n <p></p>\n <div align=\"center\"> \n  <h1><font size=\"3\">渝价[2002]771号文</font></h1> \n </div> \n <div id=\"BodyLabel\"> \n  <div align=\"center\">\n   <b>重庆市物价局&nbsp; 重庆市财政局</b>\n  </div> \n  <div align=\"center\">\n   <b>关于调整我市环境监测服务费收费标准的通知</b>\n  </div> \n  <div>\n   市环保局：\n  </div> \n  <div>\n   根据原国家物价局、财政部《关于发布环保系统行政事业性收费项目及标准的通知》（[1992]价费字178号）等有关规定，考虑到我市物价上涨水平，环境监测服务的合理耗费，以及经济发展水平和企业承受能力等情况，经市政府批准，决定对我市环境监测服务费收费标准进行调整。现将调整后的《重庆市环境监测收费管理办法》（见附件）印发给你们，请遵照执行。\n  </div> \n  <div>\n   本通知请于2003年1月1日起执行。原有规定凡与本通知不符的按本通知执行。\n  </div> \n  <div align=\"left\">\n   附件：\n  </div> \n  <div>\n   &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;重庆市环境监测收费管理办法&nbsp;&nbsp;&nbsp;\n  </div> \n  <div>\n   一、凡在我市范围内，持有《重庆市环境监测资质审查合格证》和《计量认证合格证》的环境监测机构，在完成上级下达的环境监测任务的前提下，受单位或个人委托，在以下服务范围内开展对外技术服务，按本办法收取费用：\n  </div> \n  <div>\n   1、各项环境背景值调查和环境评价；\n  </div> \n  <div>\n   2、对外单位委托的样品分析，固定和流动污染源监测以及污染纠纷、事故仲裁监测；\n  </div> \n  <div>\n   3、治理工程项目的研究设计及其环境效益分析评价；\n  </div> \n  <div>\n   4、受有关单位委托承担环境保护方面的技术咨询、技术服务、成果转让和其它方面的项目；\n  </div> \n  <div>\n   5、工业污染源成果综合服务项目，包括污染源档案、各类技术和研究报告、图集、数据库等。\n  </div> \n  <div>\n   二、收费项目及标准（见附表）\n  </div> \n  <div>\n   三、接受委托的环境监测机构，应与委托单位和个人签订合同，合同内容包括服务项目、收费项目及收费标准，明确双方的权利、义务和违约责任。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n  </div> \n  <div align=\"center\">\n   <b>收费项目及标准</b>\n  </div> \n  <div align=\"center\">\n   <b>（一）采样工作收费标准</b>\n  </div> \n  <div align=\"center\"> \n   <table border=\"1\" cellpadding=\"0\"> \n    <tbody> \n     <tr> \n      <td width=\"8%\"> \n       <div align=\"center\">\n        序号\n       </div> </td> \n      