INSERT INTO `site_info` VALUES ('112', '<h1>\n <!--<$[TITLE]>start-->重庆市环保科技动态2017年第5期\n <!--<$[TITLE]>end--></h1><div id=\"Zoom\"> \n <!--<$[CONTENT]>start--> \n <p></p>\n <p style=\"text-indent: 38.25pt\"><strong><span style=\"letter-spacing: -1pt\"><span style=\"font-size: 42pt\"><span style=\"color: red\"><span style=\"font-family: 宋体\">重庆市环保科技动态</span></span></span></span></strong></p> \n <table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" style=\"border-collapse: collapse\"> \n  <tbody> \n   <tr style=\"height: 75px\"> \n    <td style=\"border-bottom: #000000; border-left: #000000; padding-bottom: 0cm; padding-left: 5.4pt; width: 57px; padding-right: 5.4pt; height: 75px; border-top: #000000; border-right: #000000; padding-top: 0cm\"> <p><span style=\"font-size: 12pt\"><span style=\"font-family: 楷体_gb2312\">主编：</span></span></p> </td> \n    <td style=\"border-bottom: #000000; border-left: #000000; padding-bottom: 0cm; padding-left: 5.4pt; width: 159px; padding-right: 5.4pt; height: 75px; border-top: #000000; border-right: #000000; padding-top: 0cm\"> <p><span style=\"font-size: 12pt\"><span style=\"font-family: 楷体_gb2312\">市环保局科技标准处</span></span></p> <p><span style=\"font-size: 12pt\"><span style=\"font-family: 楷体_gb2312\">市环保局总工办</span></span></p> </td> \n    <td style=\"border-bottom: #000000; border-left: #000000; padding-bottom: 0cm; padding-left: 5.4pt; width: 219px; padding-right: 5.4pt; height: 75px; border-top: #000000; border-right: #000000; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center\"><span style=\"font-size: 12pt\"><span style=\"font-family: 楷体_gb2312\">&nbsp;2017</span></span><span style=\"font-size: 12pt\"><span style=\"font-family: 楷体_gb2312\">年第5</span>期（总第75</span>期）</p> </td> \n    <td style=\"border-bottom: #000000; border-left: #000000; padding-bottom: 0cm; padding-left: 5.4pt; width: 149px; padding-right: 5.4pt; height: 75px; border-top: #000000; border-right: #000000; padding-top: 0cm\"> <p align=\"right\" style=\"text-align: right\"><span style=\"font-size: 12pt\"><span style=\"font-family: 楷体_gb2312\">2017</span></span><span style=\"font-size: 12pt\"><span style=\"font-family: 楷体_gb2312\">年 5</span>月</span></p> </td> \n   </tr> \n  </tbody> \n </table> \n <p>&nbsp;</p> \n <p><strong><span style=\"font-size: 16pt\"><span style=\"font-family: 方正黑体简体\">本期导读:</span></span></strong></p> \n <p style=\"line-height: 28pt; text-indent: -19.65pt; margin: 0cm 0cm 0pt 43pt\"><span style=\"font-size: 16pt\"><span style=\"font-family: 方正楷体简体\">1. </span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正楷体简体\">市委调研我市环保产业发展</span></span></p> \n <p style=\"line-height: 28pt; text-indent: -19.65pt; margin: 0cm 0cm 0pt 43pt\"><span style=\"font-size: 16pt\"><span style=\"font-family: 方正楷体简体\">2. </span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正楷体简体\">重庆市发布场地污染防治系列技术导则</span></span></p> \n <p style=\"line-height: 28pt; text-indent: -19.65pt; margin: 0cm 0cm 0pt 43pt\"><span style=\"font-size: 16pt\"><span style=\"font-family: 方正楷体简体\">3. </span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正楷体简体\">重庆市VOCs</span>监测方法标准正式发布实施</span></p> \n <p style=\"text-indent: 22pt\"><span style=\"font-size: 16pt\"><span style=\"font-family: 方正楷体简体\">4. </span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">“</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正楷体简体\">六五</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">”</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正楷体简体\">世界环境日期间我市各地开展环保科普宣传活动</span></span></p> \n <p>&nbsp;</p> \n <p><strong><span style=\"font-size: 16pt\"><span style=\"font-family: 方正黑体简体\">简&nbsp;&nbsp; 讯：</span></span></strong></p> \n <p align=\"left\" style=\"text-align: left; text-indent: 27.75pt\"><span style=\"font-size: 15pt\"><span style=\"font-family: 方正小标宋简体\">1</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正小标宋简体\">．</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正黑体简体\">【市委调研我市环保产业发展】</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋简体\">2017</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋简体\">年5</span>月16</span>日<span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋简体\">，市委研究室李剑波处长一行赴大渡口环保科技产业园区及重庆环保产业股权投资基金管理有限公司、重庆三峰环境产业集团有限公司等环保企业调研，与环保企业代表就企业自身和全市环保产业发展遇到的问题进行了座谈交流。李处长表示，通过调研能够看出重庆发展环保产业具有坚实的基础，各部门和企业对环保产业的发展也非常重视，企业反映的问题实实在在，下一步市委研究室将结合我市环保产业发展遇到的实际问题向市委提出对策建议。</span></span></p> \n <p align=\"left\" style=\"text-align: left; text-indent: 29.75pt\"><span style=\"font-size: 16pt\"><span style=\"font-family: 方正黑体简体\">2.</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正黑体简体\">【重庆市发布场地污染防治系列技术导则】</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: 方正仿宋简体\">按照《重庆市贯彻落实土壤污染防治行动计划工作方案》的部署，由重庆市环境保护局组织编制的《污染场地治理修复环境监测技术导则》（</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: times new roman\">DB 50/T 722-2016</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: 方正仿宋简体\">）、《场地土壤环境风险评估筛选值》（</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: times new roman\">DB 50/T 723-2016</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: 方正仿宋简体\">）、《污染场地治理修复验收评估技术导则》（</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: times new roman\">DB 50/T 724-2016</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: 方正仿宋简体\">）和《场地环境调查与风险评估技术导则》（</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: times new roman\">DB 50/T 725-2016</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: 方正仿宋简体\">）等四项地方环境保护标准已经国家标准委备案通过，市质监局正式发布实施。以上四项标准的发布推进了我市场地污染防治工作的标准化进程，将促进我市场地污染防治工作更加科学合理的开展，同时，标志着我市污染场地治理水平已走在国内前列。</span></span></p> \n <p align=\"left\" style=\"text-align: left; text-indent: 27.75pt\"><span style=\"font-size: 15pt\"><span style=\"font-family: 方正小标宋简体\">3</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正小标宋简体\">．</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正黑体简体\">【重庆市VOCs</span>监测方法标准正式发布实施】</span><span style=\"font-size: 16.5pt\"><span style=\"font-family: 方正仿宋简体\">为配合</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: times new roman\">2015</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: 方正仿宋简体\">年以来我市已发布的《重庆市汽车整车制造行业表面涂装大气污染物排放标准》、《摩托车及汽车配件制造表面涂装大气污染物排放标准》、《汽车维修业大气污染物排放标准》、《包装印刷业大气污染物排放标准》和《家具制造业大气污染物排放标准》等五项地方</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: times new roman\">VOCs</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: 方正仿宋简体\">排放标准的实施，解决国家标准中专门针对固定污染源</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: times new roman\">VOCs</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: 方正仿宋简体\">的监测方法并未全部涵盖我市地方标准中相关指标的问题，重庆市环境保护局组织编制了《固定污染源废气</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: times new roman\"> VOCs</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: 方正仿宋简体\">的测定</span></span> <span style=\"font-size: 16.5pt\"><span style=\"font-family: 方正仿宋简体\">气相色谱</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: times new roman\">-</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: 方正仿宋简体\">质谱法》（</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: times new roman\">DB 50/T 679-2016</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: 方正仿宋简体\">）。近期，该地方</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: times new roman\">VOCs</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: 方正仿宋简体\">监测方法标准已通过国家标准委备案，市质监局正式发布实施。这将进一步规范我市</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: times new roman\">VOCs</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: 方正仿宋简体\">监测工作，推动我市</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: times new roman\">VOCs</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: 方正仿宋简体\">污染防治工作的科学实施。</span></span></p> \n <p><span style=\"font-size: 16.5pt\"><span style=\"font-family: times new roman\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 4.</span></span> <span style=\"font-size: 16pt\"><span style=\"font-family: 方正楷体简体\">【“</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正黑体简体\">六五”世界环境日期间我市各地开展环保科普宣传活动】</span></span><span style=\"font-size: 16.5pt\"><span style=\"font-family: 方正仿宋简体\">为推进环保科学普及，助推我市生态文明建设，围绕今年“六五”世界环境日主题，我市各区县积极开展环保科普活动，市环科院组织中小学生开展了“土壤污染防治与废旧电池处置”“校院科普宣传、动手实验实践、家庭社区宣传”系列科普活动；市环科学会组织学会会员及部分单位参观了重庆市生态环境监测中心大气超级站；市环科学会联合沙区团委组织第三军医大学、重大及市内各大高校参与了“重庆市大学生志愿者千乡万村环保科普行动”</span></span> <span style=\"font-size: 16.5pt\"><span style=\"font-family: 方正仿宋简体\">启动仪式，利用大学生暑期社会实践将环保知识、环保理念带进社区、农村，唤起大家对家园环境状况的关心。同时，三医大的志愿者们还免费为老百姓提供健康咨询和医学帮助。</span></span><span style=\"font-family: times new roman\"> </span></p> \n <p>&nbsp;</p> \n <p>&nbsp;</p> \n <p>&nbsp;</p> \n <p>&nbsp;</p> \n <p>&nbsp;</p> \n <p>&nbsp;</p> \n <p>&nbsp;</p> \n <p>&nbsp;</p> \n <p><span style=\"font-size: 15pt\"><span style=\"font-family: 仿宋_gb2312\">送:</span>局领导，机关各处室，局属各单位，市级有关部门，有关院校&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&nbsp;&nbsp;&nbsp;<span style=\"font-size: 15pt\"><span style=\"font-family: 仿宋_gb2312\">编辑：&nbsp; 陈&nbsp; 敏&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 电话：88733521&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>传真：89181803</span></p> \n <p><span style=\"font-size: 15pt\"><span style=\"font-family: 仿宋_gb2312\">审核：&nbsp; 涂宏宾&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; E-mail</span>：cqhbkjc@163.com</span></p> \n <p>&nbsp;</p> \n <p></p> \n <!--endprint--> \n <!--<$[CONTENT]>end--> \n</div>', '重庆市环保科技动态2017年第5期', '2017-06-10 00:00:00', 'http://www.cepb.gov.cn/doc/2017/06/10/162362.shtml');
INSERT INTO `site_info` VALUES ('113', '<h1>\n <!--<$[TITLE]>start-->重庆市环保规划科技动态2017年第4期\n <!--<$[TITLE]>end--></h1><div id=\"Zoom\"> \n <!--<$[CONTENT]>start--> \n <p></p>\n <p><strong><span style=\"letter-spacing: -1pt\"><span style=\"font-size: 42pt\"><span style=\"color: red\"><span style=\"font-family: 宋体\">重庆市环保规划科技动态</span></span></span></span></strong></p> \n <table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" style=\"border-collapse: collapse\"> \n  <tbody> \n   <tr style=\"height: 75px\"> \n    <td style=\"border-bottom: #000000; border-left: #000000; padding-bottom: 0cm; padding-left: 5.4pt; width: 57px; padding-right: 5.4pt; height: 75px; border-top: #000000; border-right: #000000; padding-top: 0cm\"> <p><span style=\"font-size: 12pt\"><span style=\"font-family: 楷体_gb2312\">主编：</span></span></p> </td> \n    <td style=\"border-bottom: #000000; border-left: #000000; padding-bottom: 0cm; padding-left: 5.4pt; width: 159px; padding-right: 5.4pt; height: 75px; border-top: #000000; border-right: #000000; padding-top: 0cm\"> <p><span style=\"font-size: 12pt\"><span style=\"font-family: 楷体_gb2312\">市环保局规划科技处</span></span></p> <p><span style=\"font-size: 12pt\"><span style=\"font-family: 楷体_gb2312\">市环保局总工办</span></span></p> </td> \n    <td style=\"border-bottom: #000000; border-left: #000000; padding-bottom: 0cm; padding-left: 5.4pt; width: 219px; padding-right: 5.4pt; height: 75px; border-top: #000000; border-right: #000000; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center\"><span style=\"font-size: 12pt\"><span style=\"font-family: 楷体_gb2312\">&nbsp;2017</span></span><span style=\"font-size: 12pt\"><span style=\"font-family: 楷体_gb2312\">年第4</span>期（总第74</span>期）</p> </td> \n    <td style=\"border-bottom: #000000; border-left: #000000; padding-bottom: 0cm; padding-left: 5.4pt; width: 149px; padding-right: 5.4pt; height: 75px; border-top: #000000; border-right: #000000; padding-top: 0cm\"> <p align=\"right\" style=\"text-align: right\"><span style=\"font-size: 12pt\"><span style=\"font-family: 楷体_gb2312\">2017</span></span><span style=\"font-size: 12pt\"><span style=\"font-family: 楷体_gb2312\">年 4</span>月</span></p> </td> \n   </tr> \n  </tbody> \n </table> \n <p>&nbsp;</p> \n <p><strong><span style=\"font-size: 16pt\"><span style=\"font-family: 方正黑体简体\">本期导读:</span></span></strong></p> \n <p style=\"line-height: 28pt; text-indent: -19.65pt; margin: 0cm 0cm 0pt 43pt\"><span style=\"font-size: 16pt\"><span style=\"font-family: 方正楷体简体\">1</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正楷体简体\">．<span style=\"font-family: 宋体\">市环保局认真贯彻落实市规划委员会第五次会议精神</span></span></span></p> \n <p style=\"line-height: 28pt; text-indent: -19.65pt; margin: 0cm 0cm 0pt 43pt\"><span style=\"font-size: 16pt\"><span style=\"font-family: 方正楷体简体\">2．E20</span>等多方签约共推重庆环保产业发展，污染第三方治理企业将向大渡口区聚集</span></p> \n <p><strong><span style=\"font-size: 16pt\"><span style=\"font-family: 方正黑体简体\">成果简介：</span></span></strong></p> \n <p align=\"center\" style=\"text-align: center\"><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">“</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正楷体简体\">光微波催化氧化工艺在汽车维修喷漆废气治理领域</span></span></p> \n <p align=\"center\" style=\"text-align: center\"><span style=\"font-size: 16pt\"><span style=\"font-family: 方正楷体简体\">的应用示范</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">”</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正楷体简体\">成果简介</span></span></p> \n <p>&nbsp;</p> \n <p><strong><span style=\"font-size: 16pt\"><span style=\"font-family: 方正黑体简体\">简&nbsp;&nbsp; 讯：</span></span></strong></p> \n <p style=\"line-height: 30pt; text-indent: 34.4pt\"><span style=\"font-size: 15pt\"><span style=\"font-family: 方正小标宋简体\">1</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正小标宋简体\">．【市环保局认真贯彻落实市规划委员会第五次会议精神】</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">一是及时传达学习会议精神。组织相关处室、单位对《重庆市规划委员会第五次会议纪要》进行了传达学习，并细化落实相关职责。二是做好“多规合一”试点工作。已编制完成《重庆市自然保护区发展规划》、《重庆市生态保护红线划定方案》，并纳入我市法定城乡规划全覆盖计划和“多规合一”试点；参与制定了《重庆市主城区“多规合一”编制技术指引》，提供了水环境功能区划等多项环境空间管控矢量数据。三是助推海绵城市建设。不定期对城市建成区重点河段水质开展了监督性监测工作，对主城区</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">56</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">个湖库进行了生态修复并加强监督性现场巡查，大力推动主城区集中式饮用水水源地保护区内船舶污染整治工作。四是积极参与其他专项规划的编制工作。参与</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">30</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">个重点规划的资料收集和编研工作，已为《市域美丽山水城市规划》、《重庆市水中长期供求规划》等多个规划提供了环保基础资料。</span></span></p> \n <p style=\"line-height: 30pt; text-indent: 34.4pt\"><span style=\"font-size: 15pt\"><span style=\"font-family: 方正小标宋简体\">2</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正小标宋简体\">．【E20</span>等多方签约共推重庆环保产业发展】</span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">4</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">月</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">26</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">日</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">，由大渡口区政府、重庆市环保产业协会、</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">E20</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">环境平台共同主办的“问道·马桑溪</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">-2017</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">重庆环保产业发展之路报告会”在大渡口区行政办公大楼举行。重庆市环保产业协会、大渡口区政府与</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">E20</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">环境平台共同签署了《关于共同推进大渡口区环保产业发展战略合作协议》。同时，</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">E20</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">环境平台与重庆财信环境、建桥公司签订了《关于合作发起设立工业污染第三方治理基金框架合作协议》，共同设立工业污染第三方治理基金，促进污染第三方治理环保企业向大渡口区聚集。基金初期投资规模</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">1</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">亿元，后期将达到</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">5</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">亿元，预计将带动实现环保产业营业收入</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">10</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">亿元。</span></span></p> \n <p><br clear=\"all\"> &nbsp;</p> \n <p style=\"line-height: 28pt\"><strong><span style=\"font-size: 16pt\"><span style=\"font-family: 方正黑体简体\">成果简介：</span></span></strong></p> \n <p align=\"center\" style=\"text-align: center\"><strong><span style=\"font-size: 16pt\"><span style=\"font-family: 方正楷体简体\">“光微波催化氧化工艺在汽车维修喷漆废气治理领域</span></span></strong></p> \n <p align=\"center\" style=\"text-align: center\"><strong><span style=\"font-size: 16pt\"><span style=\"font-family: 方正楷体简体\">的应用示范”成果简介</span></span></strong></p> \n <p style=\"line-height: 28pt\">&nbsp;</p> \n <p style=\"line-height: 28pt\"><strong><span style=\"font-size: 15pt\"><span style=\"font-family: 方正小标宋简体\">完成单位：</span></span></strong><span style=\"font-size: 15pt\"><span style=\"font-family: 仿宋_gb2312\">重庆三峡环保（集团）有限公司</span></span></p> \n <p style=\"line-height: 28pt; text-indent: -82.4pt; margin: 0cm 0cm 0pt 82.4pt\"><strong><span style=\"font-size: 15pt\"><span style=\"font-family: 方正小标宋简体\">主要完成人：</span></span></strong><span style=\"font-size: 15pt\"><span style=\"font-family: 仿宋_gb2312\">于东盛、江&nbsp; 郑、陈&nbsp; 梁、刘金梅、刘方芳、李有为、</span></span></p> \n <p style=\"line-height: 28pt; text-indent: 6.8pt; margin: 0cm 0cm 0pt 81.15pt\"><span style=\"font-size: 15pt\"><span style=\"font-family: 仿宋_gb2312\">范春梅、李&nbsp; 科</span></span></p> \n <p style=\"line-height: 28pt\"><strong><span style=\"font-size: 15pt\"><span style=\"font-family: 方正小标宋简体\">项目编号：</span></span></strong><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">环科字</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">2015</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">第</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">19</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">号</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.9pt\">&nbsp;</p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.9pt\"><strong><span style=\"font-size: 15pt\"><span style=\"font-family: 黑体\">一、研究背景</span></span></strong></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\"><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">为改善重庆市环境空气质量，减轻有机废气扰民，重庆市发布了《汽车维修业大气污染物排放标准》（</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">DB50/661-2016</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">）。由于汽修企业喷漆规模普遍不太大，污染物浓度低，不大适用吸附浓缩</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">+</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">催化燃烧等较复杂的治理技术，因此探索适宜汽修喷漆废气实际的治理技术很有必要。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.9pt\"><strong><span style=\"font-size: 15pt\"><span style=\"font-family: 黑体\">二、研究内容</span></span></strong></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\"><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">1</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">．采用光微波催化氧化技术，完成重庆势竹汽车销售有限公司</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">-</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">开瑞汽车巴南</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">4S</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">店喷漆车间废气治理工程，废气设计处理规模达到</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">20000m<sup>3</sup></span></span><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">/h</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">，治理后废气排放满足重庆市《汽车整车制造表面涂装大气污染物排放标准》（</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">DB50/577-2015</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">）（</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">II</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">时段标准）标准。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\"><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">2</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">．研究确定光微波催化氧化技术处理汽车维修喷涂废气满足不同标准时的适宜参数及经济指标，编制光微波催化氧化工艺处理汽车维修点喷涂废气的企业技术规范。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.9pt\"><strong><span style=\"font-size: 15pt\"><span style=\"font-family: 黑体\">三、研究结果</span></span></strong></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\"><a name=\"_Toc433372170\"><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">1</span></span></a><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">．光微波催化氧化技术适用范围为汽车维修喷漆废气等常温、中低风量、含尘量低，有机物浓度低（总</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">VOCs</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">≤</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">350mg/m<sup>3</sup></span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">）的废气治理。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\"><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">2</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">．光微波催化氧化技术对汽车维修喷漆废气中总</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">VOCs</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">的去除率为</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">70~85%</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">，能较稳定达到重庆市《汽车整车制造表面涂装大气污染物排放标准》（</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">DB50/577-2015</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">）（</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">2</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">时段标准）和《汽车维修业大气污染物排放标准》。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\"><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">3</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">．光微波催化氧化技术装置建设投资约为</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">25</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">万元</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">/</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">万</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">m<sup>3</sup></span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">，运行费用约为</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">5-8</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">元</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">/</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">万</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">m<sup>3</sup></span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">·小时；</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">占地面积较小，现场管理仅需兼职人员</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: times new roman\">1</span></span><span style=\"font-size: 15pt\"><span style=\"font-family: 方正仿宋_gbk\">名，装置可实现“即开即用，即关即停”。</span></span></p> \n <p>&nbsp;</p> \n <p>&nbsp;</p> \n <p>&nbsp;</p> \n <p>&nbsp;</p> \n <p>&nbsp;</p> \n <p>&nbsp;</p> \n <p><span style=\"font-size: 15pt\"><span style=\"font-family: 仿宋_gb2312\">送:</span>局领导，机关各处室，局属各单位，市级有关部门，有关院校&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&nbsp;&nbsp;&nbsp;<span style=\"font-size: 15pt\"><span style=\"font-family: 仿宋_gb2312\">编辑：&nbsp; 陈&nbsp; 敏&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 电话：88733521&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>传真：89181803</span></p> \n <p><span style=\"font-size: 15pt\"><span style=\"font-family: 仿宋_gb2312\">审核：&nbsp; 张&nbsp; 懿&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; E-mail</span>：cqhbkjc@163.com</span></p> \n <p>&nbsp;</p> \n <p>&nbsp;</p> \n <p>&nbsp;</p> \n <p></p> \n <!--endprint--> \n <!--<$[CONTENT]>end--> \n</div>', '重庆市环保规划科技动态2017年第4期', '2017-05-10 00:00:00', 'http://www.cepb.gov.cn/doc/2017/05/10/159270.shtml');
INSERT INTO `site_info` VALUES ('114', '<h1>\n <!--<$[TITLE]>start-->重庆市环保规划科技动态2017年第3期\n <!--<$[TITLE]>end--></h1><div id=\"Zoom\"> \n <!--<$[CONTENT]>start--> \n <p></p>\n <p><strong><span style=\"letter-spacing: -1pt\"><span style=\"font-family: 宋体\"><span style=\"color: red\"><span style=\"font-size: 42pt\">重庆市环保规划科技动态</span></span></span></span></strong></p> \n <table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" style=\"border-collapse: collapse\"> \n  <tbody> \n   <tr style=\"height: 75px\"> \n    <td style=\"border-bottom: #ece9d8; border-left: #ece9d8; padding-bottom: 0cm; padding-left: 5.4pt; width: 57px; padding-right: 5.4pt; height: 75px; border-top: #ece9d8; border-right: #ece9d8; padding-top: 0cm\"> <p><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">主编：</span></span></p> </td> \n    <td style=\"border-bottom: #ece9d8; border-left: #ece9d8; padding-bottom: 0cm; padding-left: 5.4pt; width: 159px; padding-right: 5.4pt; height: 75px; border-top: #ece9d8; border-right: #ece9d8; padding-top: 0cm\"> <p><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">市环保局规划科技处</span></span></p> <p><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">市环保局总工办</span></span></p> </td> \n    <td style=\"border-bottom: #ece9d8; border-left: #ece9d8; padding-bottom: 0cm; padding-left: 5.4pt; width: 219px; padding-right: 5.4pt; height: 75px; border-top: #ece9d8; border-right: #ece9d8; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center\"><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">&nbsp;2017</span></span><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">年第3</span>期（总第73</span>期）</p> </td> \n    <td style=\"border-bottom: #ece9d8; border-left: #ece9d8; padding-bottom: 0cm; padding-left: 5.4pt; width: 149px; padding-right: 5.4pt; height: 75px; border-top: #ece9d8; border-right: #ece9d8; padding-top: 0cm\"> <p align=\"right\" style=\"text-align: right\"><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">2017</span></span><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">年 3</span>月</span></p> </td> \n   </tr> \n  </tbody> \n </table> \n <p>&nbsp;</p> \n <p><strong><span style=\"font-family: 方正黑体简体\"><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">本期导读:</span></span></span></strong></p> \n <p style=\"line-height: 28pt; text-indent: -21.1pt; margin: 0cm 0cm 0pt 47.25pt\"><span style=\"font-family: times new roman\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">1</span></span></span><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">．环保部对生态保护红线划定工作作出新部署</span></span></p> \n <p style=\"line-height: 28pt; text-indent: -21.1pt; margin: 0cm 0cm 0pt 47.25pt\"><span style=\"font-family: times new roman\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">2</span></span></span><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">．我市加强包装印刷和家具制造业大气污染管控</span></span></p> \n <p style=\"line-height: 28pt; text-indent: -21.1pt; margin: 0cm 0cm 0pt 47.25pt\"><span style=\"font-family: times new roman\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">3</span></span></span><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">．我市农村生活污水处理设施水污染物排放标准通过专家审查</span></span></p> \n <p style=\"line-height: 28pt; text-indent: -21.1pt; margin: 0cm 0cm 0pt 47.25pt\"><span style=\"font-family: times new roman\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">4</span></span></span><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">．<span style=\"letter-spacing: -0.4pt\">国家环境保护垃圾焚烧处理与资源化工程技术中心通过预验收</span></span></span></p> \n <p style=\"line-height: 28pt; text-indent: -21.1pt; margin: 0cm 0cm 0pt 47.25pt\"><span style=\"font-family: times new roman\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">5</span></span></span><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">．我市环保产业发展经验受到兄弟省市关注</span></span></p> \n <p style=\"line-height: 28pt; text-indent: -21.1pt; margin: 0cm 0cm 0pt 47.25pt\"><span style=\"font-family: times new roman\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">6</span></span></span><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">．环保部对水专项课题管理提出要求</span></span></p> \n <p style=\"text-indent: -21.1pt; margin: 0cm 0cm 0pt 47.25pt\">&nbsp;</p> \n <p><span style=\"font-family: times new roman\"><strong><span style=\"font-family: 方正黑体简体\"><span style=\"font-size: 16pt\">成果简介：</span></span></strong></span></p> \n <p align=\"center\" style=\"text-align: center; line-height: 28pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">“三峡库区景观生态风险评价研究”成果简介</span></span></span></p> \n <p><strong><span style=\"font-family: 方正黑体简体\"><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">简&nbsp;&nbsp; 讯：</span></span></span></strong></p> \n <p style=\"text-indent: 29.85pt\"><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">1</span></span><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">．【环保部对生态保护红线划定工作作出新部署】</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">2017</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">年</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">3</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">月</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">28</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">－</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">29</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">日，环境保护部在成都市举办</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">2017</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">年第一期生态保护红线专题培训班，培训讲解《关于划定并严守生态保护红线的若干意见》等，并部署下一步工作。环境保护部副部长黄润秋出席培训班并讲话。他指出，要准确把握《若干意见》核心要义和划定并严守生态保护红线的目标任务，加快划定工作，让红线划得实、能落地、管得好。要划好一条红线，即深刻理解一条红线管控重要生态空间的内涵；建立一个机制，即建立一个环保、发展改革部门牵头的跨部门红线管理协调机制；落实三个阶段目标任务，特别是京津冀区域、长江经济带沿线</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">14</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">个省（市）要在</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">2017</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">年底前划定生态保护红线。既要科学合理划定生态保护红线，又要</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">“</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">事前严防、事中严管、事后奖惩</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">”</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">，全过程严守生态保护红线。</span></span></p> \n <p><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">&nbsp;&nbsp; 2</span></span><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">．【我市加强包装印刷和家具制造业大气污染管控】</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 16pt\">2017</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">年</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 16pt\">3</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">月</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 16pt\">23</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">日</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">，市政府批准实施《包装印刷业大气污染物排放标准》和《家具制造业大气污染物排放标准》两项地方环保标准，加严了排放限值，增强了对两行业的大气污染管控。至此，我市已针对工业挥发性有机物（</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 16pt\">VOCs</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">）排放重点行业发布实施了</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 16pt\">5</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">项地方环保标准，标准体系基本完善，为改善空气质量、减少废气污染扰民提供了有力支撑。</span></span></p> \n <p><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">&nbsp;&nbsp; 3</span></span><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">．【我市农村生活污水处理设施水污染物排放标准通过专家审查】</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">为改善农村环境，促进农村生活污水处理设施规范运行，市环保局组织编制了《农村生活污水处理设施水污染物排放标准》。该标准结合</span></span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">我市山地地区水环境容量较大，农村地区经济欠发达，但又处于三峡水库敏感区等实际，分级分类提出了针对性的排放限值要求。</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 16pt\">2017</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">年</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 16pt\">3</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">月</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 16pt\">2</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">日</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">，该标准通过了市质监局组织的专家审查，将作为强制性地方标准报市政府批准发布。</span></span></p> \n <p style=\"line-height: 27pt; text-indent: 29.55pt\"><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">4</span></span><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">．【国家环境保护垃圾焚烧处理与资源化工程技术中心通过预验收】</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">按照国家环境保护工程技术中心建设管理要求，</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">2017</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">年</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">3</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">月</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">2</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">日</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">，市环保局组织专家组对重庆三峰环境产业集团有限公司承担建设的国家环境保护垃圾焚烧处理与资源化工程技术中心（以下简称“工程技术中心”）进行了预验收。专家组一致认为工程技术中心超额完成了建设任务，达到了预期目标，同意通过预验收。下一步将报请环保部予以正式验收。</span></span></p> \n <p style=\"text-indent: 37.2pt\"><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">5</span></span><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">．【我市环保产业发展经验受到兄弟省市关注】</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">近日，湖南省人大环资委、吉林省环境保护厅先后来我市调研环保产业园区建设状况、交流环保产业发展经验。他们对我市把环保产业作为战略性新兴产业的重要组成给予高度重视和积极支持，率先创新环保产业发展投融资机制，扎实推进环保科技创新表示了赞赏。</span></span></p> \n <p><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">&nbsp;&nbsp;&nbsp; 6</span></span><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">．【环保部对水专项课题管理提出要求】</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 16pt\">3</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">月</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 16pt\">3</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">日</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">，在水专项</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 16pt\">2016</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">年中期评估暨年度检查通报会上，环保部对水专项课题管理及研究工作提出三点要求。一是严格落实资金管理主体责任。进一步落实财务法人责任制，加强资金管理，规范资金使用。二是及时督促课题进展。牵头单位要强化内部管理，定期调度，把握进度及时间节点，确保项目按期竣工验收。三是加强科技成果凝练。保证示范工程真实性，重视研究成果总结，确保科技成果按期产出。</span></span></p> \n <p>&nbsp;</p> \n <p><strong><span style=\"font-family: 方正黑体简体\"><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">成果简介：</span></span></span></strong></p> \n <p style=\"text-indent: 55.35pt\"><strong><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">“三峡库区景观生态风险评价研究”成果简介</span></span></span></strong></p> \n <p align=\"left\" style=\"text-align: left\">&nbsp;</p> \n <p align=\"left\" style=\"text-align: left\"><strong><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">完成单位</span></span></strong><span style=\"font-family: 方正仿宋简体\">：&nbsp;&nbsp;&nbsp; </span><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-size: 15pt\">重庆工商大学</span></span></p> \n <p style=\"text-indent: -95.9pt; margin: 0cm 0cm 0pt 95.9pt\"><strong><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">主要完成人：</span></span></strong><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-size: 15pt\">周启刚、马泽忠、张晓媛、陈&nbsp; 倩、王福海、陈&nbsp; 丹、</span></span></p> \n <p style=\"text-indent: -4.35pt; margin: 0cm 0cm 0pt 97.6pt\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-size: 15pt\">李&nbsp; 辉、焦&nbsp; 欢、国洪磊</span></span></p> \n <p style=\"text-indent: -88.1pt; margin: 0cm 0cm 0pt 88.1pt\"><strong><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">项目编号</span></span></strong><strong><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">：</span></span></strong><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-size: 16pt\">环科字</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">2016CF</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">第</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">08</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">号</span></span></p> \n <p>&nbsp;</p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 30.1pt\"><strong><span style=\"font-family: 黑体\"><span style=\"font-size: 15pt\">一、研究</span></span></strong><strong><span style=\"font-family: 黑体\"><span style=\"font-size: 15pt\">背景</span></span></strong></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 30pt\"><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">三峡工程建成后，其对库区生态环境的影响会造成库区景观格局发生变化，通过评价景观生态风险可以为区域生态综合风险防范提供决策依据、有效指引区域景观格局优化与管理。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 30.1pt\"><strong><span style=\"font-family: 黑体\"><span style=\"font-size: 15pt\">二、研究内容</span></span></strong></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 30pt\"><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">（一）景观生态环境变化研究：利用</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">2000</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">、</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">2004</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">、</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">2007</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">、</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">2010</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">、</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">2012</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">和</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">2014</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">年</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">TM</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">影像数据，结合遥感和</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">GIS</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">技术，划分生态景观类型，并综合分析景观生态环境要素的动态变化过程。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 30pt\"><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">（二）景观生态风险评价指标选取：从反映景观类型空间耐受性特征的指标中选取评价指标体系。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 30pt\"><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">（三）景观生态风险的模型评价：引入正态云评价模型，对景观生态风险程度（分为低风险、较低风险、中度风险、较高风险、高风险五个等级）进行评价。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 30pt\"><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">（四）景观生态风险预测：基于景观生态风险评价结果，利用灰色模型对</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">2016</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">、</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">2017</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">和</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">2018</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">年的生态风险进行模拟。综合分析区域景观生态风险变化趋势，对景观生态风险进行预测，并提出相关政策与建议。</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\"> </span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 30.1pt\"><strong><span style=\"font-family: 黑体\"><span style=\"font-size: 15pt\">三、研究</span></span></strong><strong><span style=\"font-family: 黑体\"><span style=\"font-size: 15pt\">成果</span></span></strong></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 30pt\"><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">（一）库区景观生态环境变化情况。</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">2000-2014</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">年，数量上，库区耕地景观、林地景观呈现明显下降态势，草地景观主要呈波动式降低态势，水域景观面积呈现明显增长态势，建设用地景观呈现逐年递增态势。空间上，三峡库区耕地景观主要分布在库腹和库尾，林地景观主要分布在库首和库腹较多，草地景观主要分布在库腹地区，水域景观主要由长江及其支流和各大湖泊、坑塘所构成，建设用地景观以多中心的空间形态，即主要分布在各区县的建成区。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 30pt\"><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">（二）景观生态风险评价指标体系及指标变化情况。选择斑块密度、形状指数、周长面积分维数、香农多样性指数</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">4</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">个指标来评价景观生态风险。分析发现，各区县斑块密度变化存在显著差异，且呈波动式变化；形状指数变化最为剧烈的为开县、云阳；周长面积分维数整体呈降低</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">-</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">上升</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">-</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">降低趋势；香农多样性指数变化较小，变化幅度最大的为万州、主城九区、夷陵和秭归，其余区县的变化程度相对平稳。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 30pt\"><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">（三）景观生态风险评价结果。</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">2000-2014</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">年，三峡库区景观生态风险总体处于较低风险和中度风险，景观风险之间的转化主要在相邻等级之间进行。空间上，三峡库区库首和库尾地区主要以较低风险为主，库腹区域主要以中度风险为主。库首保持较为稳定的状态，库腹和库尾处于动态变化过程。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 30pt\"><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">（四）景观生态风险预测结果。</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">预测</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">2016</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">年，三峡库区</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">17</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">个区域景观生态风险分别为较低风险</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">6</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">个、中度风险</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">6</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">个和较高风险</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">5</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">个。</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">2017</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">年风险等级未发生变化。</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">2018</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">年，仅巴东县由中度风险上升为较高风险，其余保持不变。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 30pt\"><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">（五）对策建议。</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">一是深入开展区域协调发展政策研究</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">。通过制定库区社会经济与生态保护协调发展的政策，化解人地矛盾、发展与保护矛盾等影响库区景观生态风险的根本原因。二是加强库区景观生态环境监测。采用遥感监测等多种手段，每半年或一年对库区景观生态环境进行监测。三是强化生态保护红线落地与监管。通过划定及严守生态保护红线，刚性管理库区重要的自然保护区、生态涵养区、水土流失重点区等，突出建设林地景观大斑块，降低景观破碎度，保护库区景观生态重要区。四是进一步创新库区景观生态管理机制，明晰管理主体以及管理职责。</span></span></p> \n <p>&nbsp;</p> \n <p>&nbsp;</p> \n <p>&nbsp;</p> \n <p>&nbsp;</p> \n <p><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-size: 15pt\">送:</span><span style=\"font-size: 20px\">局领导，机关各处室，局属各单位，市级有关部门，有关院校</span></span></p> \n <p><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-size: 15pt\">编辑：&nbsp; 陈&nbsp; 敏&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 电话：88733521&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 20px\">传真：89181803</span></span></p> \n <p><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-size: 15pt\">审核：&nbsp; 张&nbsp; 懿&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; E-mail</span>：<span style=\"font-size: 20px\"><a href=\"mailto:cqhbkjc@163.com\">cqhbkjc@163.com</a></span></span></p> \n <p></p> \n <!--endprint--> \n <!--<$[CONTENT]>end--> \n</div>', '重庆市环保规划科技动态2017年第3期', '2017-04-12 00:00:00', 'http://www.cepb.gov.cn/doc/2017/04/12/157723.shtml');
INSERT INTO `site_info` VALUES ('115', '<h1>\n <!--<$[TITLE]>start-->重庆市环保规划科技动态2017年第2期\n <!--<$[TITLE]>end--></h1><div id=\"Zoom\"> \n <!--<$[CONTENT]>start--> \n <p></p>\n <p><strong><span style=\"letter-spacing: -1pt\"><span style=\"font-family: 宋体\"><span style=\"color: red\"><span style=\"font-size: 42pt\">重庆市环保规划科技动态</span></span></span></span></strong></p> \n <table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" style=\"border-collapse: collapse\"> \n  <tbody> \n   <tr style=\"height: 75px\"> \n    <td style=\"padding-bottom: 0cm; padding-left: 5.4pt; width: 57px; padding-right: 5.4pt; height: 75px; padding-top: 0cm\"> <p style=\"line-height: 28pt\"><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">主编：</span></span></p> </td> \n    <td style=\"padding-bottom: 0cm; padding-left: 5.4pt; width: 159px; padding-right: 5.4pt; height: 75px; padding-top: 0cm\"> <p style=\"line-height: 28pt\"><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">市环保局规划科技处</span></span></p> <p style=\"line-height: 28pt\"><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">市环保局总工办</span></span></p> </td> \n    <td style=\"padding-bottom: 0cm; padding-left: 5.4pt; width: 219px; padding-right: 5.4pt; height: 75px; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center; line-height: 28pt\"><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">&nbsp;2017</span></span><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">第2</span>期（总第72</span>期）</p> </td> \n    <td style=\"padding-bottom: 0cm; padding-left: 5.4pt; width: 149px; padding-right: 5.4pt; height: 75px; padding-top: 0cm\"> <p align=\"right\" style=\"text-align: right; line-height: 28pt\"><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">2017</span></span><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">年 2</span>月</span></p> </td> \n   </tr> \n  </tbody> \n </table> \n <p style=\"line-height: 28pt\">&nbsp;</p> \n <p style=\"line-height: 28pt\"><strong><span style=\"font-family: 方正黑体简体\"><span style=\"font-size: 16pt\">本期导读:</span></span></strong></p> \n <p style=\"line-height: 28pt; text-indent: -19.65pt; margin-left: 43pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">1</span></span><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">．中办、国办印发《关于划定并严守生态保护红线的若干意见》</span></span></p> \n <p style=\"line-height: 28pt; text-indent: -19.65pt; margin-left: 43pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">2</span></span><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">．我市生态保护红线监控将借力“千里眼”</span></span></p> \n <p style=\"line-height: 28pt; text-indent: -19.65pt; margin-left: 43pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">3．我市13家企业获评环保产业AAA级信用等级</span></span></p> \n <p style=\"line-height: 28pt; text-indent: -19.65pt; margin-left: 43pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">4</span></span><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">．社团组织助力我市环保标准咨询</span></span></p> \n <p style=\"line-height: 28pt; text-indent: -19.65pt; margin-left: 43pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">5</span></span><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">．我市着力构建地方环保技术选择指南体系</span></span></p> \n <p style=\"line-height: 28pt\">&nbsp;</p> \n <p style=\"line-height: 28pt\"><strong><span style=\"font-family: 方正黑体简体\"><span style=\"font-size: 16pt\">成果简介：</span></span></strong></p> \n <p align=\"center\" style=\"text-align: center; line-height: 28pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">“规模化养殖废水深度处理脱氮关键技术攻关与应用研究”成果简介</span></span></p> \n <p style=\"line-height: 28pt\">&nbsp;</p> \n <p style=\"line-height: 28pt\"><strong><span style=\"font-family: 方正黑体简体\"><span style=\"font-size: 16pt\">简&nbsp;&nbsp; 讯：</span></span></strong></p> \n <p style=\"text-indent: 27.6pt\"><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">1</span></span><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">．【中办、国办印发《关于划定并严守生态保护红线的若干意见》】</span></span><span style=\"font-size: 16pt\">2017</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">年</span></span><span style=\"font-size: 16pt\">1</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">月</span></span><span style=\"font-size: 16pt\">25</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">日</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">，中办、国办印发《关于划定并严守生态保护红线的若干意见》（以下简称《意见》）。这是党中央、国务院在新时期新形势下作出的一项重大决策，是推进国土空间用途管制、守住国家生态安全底线、建设生态文明的一项基础性制度安排。《意见》突出体现“山水林田湖是一个生命共同体”的理念，遵循国土空间用途管制的改革思路，坚持源头严防、过程严管、后果严惩。《意见》提出，要以改善生态环境质量为核心，以保障和维护生态功能为主线，实现一条红线管控重要生态空间。《意见》明确，</span></span><span style=\"font-size: 16pt\">2017</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">年京津冀区域、长江经济带沿线各省（市）要划定生态保护红线。</span></span></p> \n <p style=\"line-height: 28pt; text-indent: 23.2pt; margin-left: 0.1pt\"><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">2</span></span><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">．【我市生态保护红线监管将借力“千里眼”】</span></span><span style=\"font-size: 16pt\">2017</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">年</span></span><span style=\"font-size: 16pt\">2</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">月</span></span><span style=\"font-size: 16pt\">13</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">日</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">，环保部卫星环境应用中心与市环保局签订《关于加强重庆市生态环境遥感与应用合作协议》，共同建设“环保部卫星环境应用中心重庆环境遥感应用基地”。双方将在生态保护红线监管、三峡库区生态环境保护等领域开展遥感监测与环保大数据综合应用合作。今后我市生态保护红线监管将借力国家环境卫星的“千里眼”，建立监管体系，及时发现、核查和处理破坏生态保护红线的行为。</span></span></p> \n <p style=\"text-indent: 27.6pt\"><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">3. </span></span><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">【我市13家企业获评环保产业AAA级信用等级</span></span>】<span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">按照民政部、中央编办等八部委《关于推进行业协会商会诚信自律建设工作的意见》要求，</span></span><span style=\"font-size: 16pt\">2011</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">－</span></span><span style=\"font-size: 16pt\">2016</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">年，中国环保产业协会在全国范围内完成了八批次环保产业行业信用等级评价工作，共有</span></span><span style=\"font-size: 16pt\">404</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">家（次）企业获评</span></span><span style=\"font-size: 16pt\">A</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">级以上信用等级，其中</span></span><span style=\"font-size: 16pt\">AAA</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">级</span></span><span style=\"font-size: 16pt\">273</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">家。截至</span></span><span style=\"font-size: 16pt\">2016</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">年底，我市共有</span></span><span style=\"font-size: 16pt\">16</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">家企业入围，其中</span></span><span style=\"font-size: 16pt\">13</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">家企业获评</span></span><span style=\"font-size: 16pt\">AAA</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">级信用等级，较</span></span><span style=\"font-size: 16pt\">2015</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">年新增</span></span><span style=\"font-size: 16pt\">1</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">家，处于西部领先。</span></span></p> \n <p style=\"line-height: 28pt; text-indent: 23.2pt; margin-left: 1.45pt\"><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">4</span></span><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">．【社团组织助力我市环保标准咨询】</span></span><span style=\"font-size: 16pt\">2</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">月</span></span><span style=\"font-size: 16pt\">21</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">日</span></span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">，重庆市环境科学学会环保标准专业委员会召开</span></span><span style=\"font-size: 16pt\">2017</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">年工作会。会议商定了</span></span><span style=\"font-size: 16pt\">2017</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">年重点工作，明确了具体内容，制定了工作计划。专委会重点将为我市环保标准和技术指南的研究、制修订、培训交流、实施评估等工作提供专业的第三方咨询服务。</span></span></p> \n <p style=\"text-indent: 27.75pt\"><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">5</span></span><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">．【我市着力构建地方环保技术选择指南体系】</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">为引导和服务于我市重点领域污染治理适宜技术选择，</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">推进污染治理技术进步，市环保局正着力构建适宜重庆实际的污染治理技术选择体系。目前已在农村生活污水、小城镇生活污水、畜禽养殖粪污、典型工业有机废气治理等</span></span><span style=\"font-size: 16pt\">4</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">个领域发布了适宜技术选择指南，预计</span></span><span style=\"font-size: 16pt\">2017</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">年将发布电镀废水治理、锅炉烟气脱硝、水生态修复等</span></span><span style=\"font-size: 16pt\">3</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">个领域的</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">适宜技术选择指南。今后将在征求各方需求的基础上，进一步完善地方环保技术选择指南体系。</span></span></p> \n <p>&nbsp;</p> \n <p>&nbsp;</p> \n <p><br clear=\"all\"> &nbsp;</p> \n <p><strong><span style=\"font-family: 方正黑体简体\"><span style=\"font-size: 16pt\">成果简介：</span></span></strong></p> \n <p align=\"center\" style=\"text-align: center; line-height: 28pt\"><strong><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">“规模化养殖废水深度处理脱氮关键技术攻关与应用研究”成果简介</span></span></strong></p> \n <p align=\"left\" style=\"text-align: left\">&nbsp;</p> \n <p><strong><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">完成单位：</span></span></strong><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">重庆大学 </span></span></p> \n <p style=\"text-indent: -81.55pt; margin-left: 81.55pt\"><strong><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">主要完成人</span></span></strong><strong><span style=\"font-family: 宋体\">：</span></strong><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">张&nbsp; 智、廖建华、刘国军、吴浩楠、张可平、周文龙、</span></span></p> \n <p style=\"text-indent: -3.55pt; margin-left: 84.05pt\"><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">梅子凡、刘朝富、黎中华、韩闰林、翁张帆</span></span></p> \n <p style=\"text-indent: -81.55pt; margin-left: 81.55pt\"><strong><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">项目编号：</span></span></strong><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">环科字</span></span><span style=\"font-size: 16pt\"> 2015</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">第</span></span><span style=\"font-size: 16pt\">24</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">号</span></span></p> \n <p style=\"text-indent: -87.05pt; margin-left: 87.05pt\">&nbsp;</p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.9pt\"><strong><span style=\"font-family: 黑体\"><span style=\"font-size: 15pt\">一、研究背景</span></span></strong></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\"><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">我市规模化养殖业产生大量畜禽粪污，污染物排放浓度高，对环境影响较大。常规的废水处理方法效率不稳定，存在出水总氮浓度高、达标不稳定等问题。因此，亟需研究适用于规模化养殖废水的深度脱氮处理技术，提高污染物去除率，合理控制处理成本，以减轻规模化养殖污染影响。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.9pt\"><strong><span style=\"font-family: 黑体\"><span style=\"font-size: 15pt\">二、研究内容</span></span></strong></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\"><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">1</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">．规模化养殖粪污氮排放规律与水质特性研究。研究粪污源对沼液水质特性的影响、沼气化工艺与运行对沼液水质特性影响。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\"><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">2</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">．基于深度处理脱氮的预处理技术模式及参数研究。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\"><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">3</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">．基于深度处理脱氮的沼气化技术模式及最佳工艺参数研究。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\"><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">4</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">．养殖沼液多级</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">AO</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">反应器深度脱氮运行模式与效能研究。研究养殖沼液多级</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">AO</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">技术深度处理脱氮技术，包括运行模式、调控技术和控制参数等。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\"><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">5. </span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">技术应用。选择潼南温氏畜牧有限公司花岩猪场</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">600m<sup>3</sup></span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">/d</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">能力的养殖废水处理工程（</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">处理工艺为：预处理</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">+</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">厌氧处理</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">+</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">多级</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">A/O</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">）进行技术改造应用，将出水主要指标从原《畜禽养殖业污染物排放标准》（</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">GB 18956-2001</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">）提升到《污水综合排放标准》（</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">GB 8978-1996</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">）一级标准，并通过参数优化降低处理成本。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.9pt\"><strong><span style=\"font-family: 黑体\"><span style=\"font-size: 15pt\">三、研究成果</span></span></strong></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\"><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">1</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">．规模化养猪场废水排放规律：漏缝板干清粪工艺综合废水产生量约</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">3.5</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">（冬）～</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">4.5</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">（夏）</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">m³/</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">百头</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">·d</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">，原水</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">COD 6000</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">～</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">8000mg/L</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">，</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">NH<sub>3</sub>-N 400</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">～</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">550mg/L</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">，</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">TP 50</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">～</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">60mg/L</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">；水冲</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">/</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">水泡粪工艺综合废水产生量约</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">5.5</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">（冬）～</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">7.5</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">（夏）</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">m³/</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">百头</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">·d</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">，原水</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">COD 15000</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">～</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">40000mg/L</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">，</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">NH<sub>3</sub>-N 500</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">～</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">700mg/L</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">，</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">TP ≥60mg/L</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\"><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">2</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">．处理工艺：多级</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">A/O</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">系统低溶解氧环境下同步（短程）硝化反硝化技术对处理高污染物浓度、</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">C/N</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">比失衡的养殖沼液具有良好适应性。溶解氧控制方式采用正溶解氧浓度梯度工况时处理效能最优。该工况主要运行参数为：顺反应流程溶解氧由</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">0.3mg/L</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">升至</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">1.5mg/L</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">；一级</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">A/O</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">系统</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">MLSS 3</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">～</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">4g</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">/L</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">，二级</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">A/O</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">系统</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">MLSS 2</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">～</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">3.5g</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">/L</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">（</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">MLSS</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">夏季取低值，冬季取高值）；污泥龄</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">20</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">～</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">25d</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">；污泥回流比</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">50%</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">～</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">100%</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">；混合液回流比</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">100%</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">～</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">200%</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\"><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">3</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">．出水水质：工艺参数调整后，工程出水指标满足</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">COD≤100mg/L</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">、</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">NH<sub>3</sub>-N≤20mg/L</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">和</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">TN≤60mg/L</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">，较调控前（</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">COD 220</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">～</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">280mg/L</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">、</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">NH<sub>3</sub>-N 20</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">～</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">60mg/L</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">，</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">TN 60</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">～</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">160mg/L</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">）分别降低约</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">60%</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">、</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">57%</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">和</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">53 %</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\"><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">4</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">．经济指标：</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">①</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">吨水耗电量：约为</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">2.82</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">～</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">3.69kW?h</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">。</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">②</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">废水处理直接成本</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">(</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">电费</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">+</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">药剂费</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">)</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">：约</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">3.06</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">（夏）～</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">4.23</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">（冬）元</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">/m³</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">。较调控前降低约</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">0.23</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">元</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">/m³</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\"><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">5</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">．技术推广：将相关技术在正大广安猪场（同期存栏育肥猪</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">2000</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">头，废水处理规模</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">150 m³</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">/d</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">）和温氏柏梓猪场（同期存栏育肥猪</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">5000</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">头，废水处理规模</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">300 m³</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">/d</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">）推广检验。调控后，广安猪场出水水质由满足《畜禽养殖业污染物排放标准》</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">(GB18596-2001)</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">提升至满足《农田灌溉水质标准》</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">(GB 5084-2005)</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">的旱作灌溉要求；柏梓猪场系统出水水质也有所改善。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\"><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">研究成果表明：采用多级</span></span><span style=\"font-family: times new roman\"><span style=\"font-size: 15pt\">A/O</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">系统低溶解氧环境下同步（短程）硝化反硝化技术处理规模化养猪废水，通过精细调控，优化运行参数，实现同步（短程）硝化反硝化，系统脱氮效果明显，出水水质改善明显，运行成本可接受。</span></span></p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\">&nbsp;</p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\">&nbsp;</p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\">&nbsp;</p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\">&nbsp;</p> \n <p style=\"text-justify: inter-ideograph; text-align: justify; text-indent: 27.75pt\">&nbsp;</p> \n <p>&nbsp;</p> \n <p style=\"line-height: 28pt\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-size: 15pt\">送:</span><span style=\"font-size: 18px\">局领导，机关各处室，局属各单位，市级有关部门，有关院校</span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&nbsp;&nbsp;&nbsp;<span style=\"font-family: 仿宋_gb2312\"><span style=\"font-size: 15pt\">编辑：&nbsp; 陈&nbsp; 敏&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 电话：88733521&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 18px\">传真：89181803</span></span></p> \n <p style=\"line-height: 28pt\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-size: 15pt\">审核：&nbsp; 张&nbsp; 懿&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; E-mail</span>：<span style=\"font-size: 18px\">cqhbkjc@163.com</span></span></p> \n <p style=\"line-height: 28pt\">&nbsp;</p> \n <p></p> \n <!--endprint--> \n <!--<$[CONTENT]>end--> \n</div>', '重庆市环保规划科技动态2017年第2期', '2017-03-13 00:00:00', 'http://www.cepb.gov.cn/doc/2017/03/13/155501.shtml');
INSERT INTO `site_info` VALUES ('116', '<h1>\n <!--<$[TITLE]>start-->重庆市环保规划科技动态2017年1月\n <!--<$[TITLE]>end--></h1><div id=\"Zoom\"> \n <!--<$[CONTENT]>start--> \n <p></p>\n <p>&nbsp;</p> \n <p><strong><span style=\"letter-spacing: -1pt\"><span style=\"font-family: 宋体\"><span style=\"color: red\"><span style=\"font-size: 42pt\">重庆市环保规划科技动态</span></span></span></span></strong></p> \n <table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" style=\"border-collapse: collapse\"> \n  <tbody> \n   <tr style=\"height: 75px\"> \n    <td style=\"padding-bottom: 0cm; padding-left: 5.4pt; width: 57px; padding-right: 5.4pt; height: 75px; padding-top: 0cm\"> <p style=\"line-height: 28pt\"><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">主编：</span></span></p> </td> \n    <td style=\"padding-bottom: 0cm; padding-left: 5.4pt; width: 159px; padding-right: 5.4pt; height: 75px; padding-top: 0cm\"> <p style=\"line-height: 28pt\"><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">市环保局规划科技处</span></span></p> <p style=\"line-height: 28pt\"><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">市环保局总工办</span></span></p> </td> \n    <td style=\"padding-bottom: 0cm; padding-left: 5.4pt; width: 219px; padding-right: 5.4pt; height: 75px; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center; line-height: 28pt\"><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">&nbsp;2017</span></span><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">第1</span>期（总第71</span>期）</p> </td> \n    <td style=\"padding-bottom: 0cm; padding-left: 5.4pt; width: 149px; padding-right: 5.4pt; height: 75px; padding-top: 0cm\"> <p align=\"right\" style=\"text-align: right; line-height: 28pt\"><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">2017</span></span><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">年 1</span>月</span></p> </td> \n   </tr> \n  </tbody> \n </table> \n <p style=\"line-height: 28pt\">&nbsp;</p> \n <p style=\"line-height: 28pt\"><strong><span style=\"font-family: 方正黑体简体\"><span style=\"font-size: 16pt\">本期导读:</span></span></strong></p> \n <p style=\"line-height: 28pt; text-indent: -19.65pt; margin-left: 43pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">1</span></span><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">．我市召开“十三五”生态文明建设规划新闻发布会</span></span></p> \n <p style=\"line-height: 28pt; text-indent: -19.65pt; margin-left: 43pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">2</span></span><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">．各区县积极贯彻落实“十三五”生态文明建设规划</span></span></p> \n <p style=\"line-height: 28pt; text-indent: -19.65pt; margin-left: 43pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">3</span></span><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">．环保产业调查培训</span></span></p> \n <p style=\"line-height: 28pt; text-indent: -19.65pt; margin-left: 43pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">4</span></span><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">．大足区与中科院联手打造高新环保产业基地</span></span></p> \n <p style=\"line-height: 28pt; text-indent: -19.65pt; margin-left: 43pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">5</span></span><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">．重庆三峰环境产业集团有限公司技术创新取得丰硕成果</span></span></p> \n <p style=\"line-height: 28pt\">&nbsp;</p> \n <p style=\"line-height: 28pt\"><strong><span style=\"font-family: 方正黑体简体\"><span style=\"font-size: 16pt\">成果简介：</span></span></strong></p> \n <p align=\"center\" style=\"text-align: center; line-height: 28pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">“重庆市主城区空气质量预报模型的研究与应用”成果简介</span></span></p> \n <p style=\"line-height: 28pt\">&nbsp;</p> \n <p style=\"line-height: 28pt\"><strong><span style=\"font-family: 方正黑体简体\"><span style=\"font-size: 16pt\">简&nbsp;&nbsp; 讯：</span></span></strong></p> \n <p style=\"line-height: 28pt; text-indent: 27.75pt\"><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">1</span></span><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">．【我市召开“十三五”生态文明建设规划新闻发布会】</span></span><span style=\"font-size: 16pt\">1</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">月</span></span><span style=\"font-size: 16pt\">24</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">日</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">，市政府新闻办公室召开新闻发布会，市环保局副局长余国东对《重庆生态文明建设“十三五”规划》的总体思路、规划特点、约束性指标及主要措施等进行了解读。《规划》提出到</span></span><span style=\"font-size: 16pt\">2020</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">年，把重庆建设成为长江上游生态文明先行示范带的核心区，基本建成碧水青山、绿色低碳、人文厚重、和谐宜居的生态文明城市，使绿色成为重庆发展的本底，使重庆成为山清水秀的美丽之地。</span></span></p> \n <p style=\"line-height: 28pt; text-indent: 27.75pt\"><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">2</span></span><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">．【各区县积极贯彻落实“十三五”生态文明建设规划】</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">目前，全市</span></span><span style=\"font-size: 16pt\">38</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">个区县和万盛经开区、两江新区中已有</span></span><span style=\"font-size: 16pt\">33</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">个区县政府印发了各自区域的“十三五”生态文明建设规划，</span></span><span style=\"font-size: 16pt\">5</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">个区县待印发，</span></span><span style=\"font-size: 16pt\">2</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">个区县正在编制中。区县的规划均认真贯彻我市生态文明建设“十三五”规划的要求，对规划的重点任务进行了分解，责任落实到各个单位。</span></span></p> \n <p style=\"line-height: 28pt; text-indent: 27.75pt\"><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">3.</span></span> <span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">【我市召开2017</span><span style=\"font-size: 20px\">年环保产业调查技术培训会</span>】</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">为促进环保产业发展，进一步摸清底数，我市建立了环保产业调查制度。为确保</span></span><span style=\"font-size: 16pt\">2016</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">年度全市环保产业调查工作的顺利实施，市环保局组织召开了</span></span><span style=\"font-size: 16pt\">2017</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">年环保产业调查技术培训会，对各区县环保局和部分环保企业近百名人员进行了培训。</span></span></p> \n <p style=\"line-height: 28pt; text-indent: 27.75pt\"><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">4.</span></span> <span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">【大足区与中科院联手打造高新环保产业基地】</span></span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">大足区与中科院联手打造的高新环保产业基地是中科院在全国布局的首个综合环保产业基地。基地主要致力于环境治理，涵盖水、气、土壤、固废等各领域。建成后，预计将实现产值超过</span></span><span style=\"font-size: 16pt\">50</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">亿元，能够形成环保产、学、研、用的商业化产业模式，促进快速形成环保产业核心技术，提高我市环保技术竞争力。日前，首批</span></span><span style=\"font-size: 16pt\">6</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">家企业已顺利入驻，将带动近</span></span><span style=\"font-size: 16pt\">10</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">亿元产值的环保产业聚集。</span></span></p> \n <p style=\"line-height: 28pt; text-indent: 27.75pt\"><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">5</span></span><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">．【</span></span><span style=\"letter-spacing: 0.5pt\"><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 16pt\">重庆三峰环境产业集团有限公司技术创新取得丰硕成果</span></span></span><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">】</span></span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">重庆三峰环境产业集团有限公司以获批建设</span></span><span style=\"font-size: 16pt\">“</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">国家环境保护垃圾焚烧处理与资源化工程技术中心</span></span><span style=\"font-size: 16pt\">”</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">为契机，大力开展科技创新，取得丰硕成果。工程中心建设期间，公司在生活垃圾清洁焚烧、烟气净化、渗滤液处理和危废处理等</span></span><span style=\"font-size: 16pt\">4</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">个领域承担了</span></span><span style=\"font-size: 16pt\">5</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">项省部级科研项目；主编了</span></span><span style=\"font-size: 16pt\">6</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">项国家、地方和行业标准；开展了</span></span><span style=\"font-size: 16pt\">9</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">项关键技术研究，完成了</span></span><span style=\"font-size: 16pt\">3</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">项技术转化，取得</span></span><span style=\"font-size: 16pt\">17</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">项专利；获得了包括重庆市科技进步一等奖在内的</span></span><span style=\"font-size: 16pt\">14</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">个奖项；在美国、印度、埃塞俄比亚及国内多省市建设了</span></span><span style=\"font-size: 16pt\">80</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">个垃圾焚烧示范项目</span></span><span style=\"font-size: 16pt\">160</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">条焚烧线；完成了</span></span><span style=\"font-size: 16pt\">19</span><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">个渗滤液处理示范项目。</span></span></p> \n <p style=\"line-height: 28pt\">&nbsp;</p> \n <p><br clear=\"all\"> &nbsp;</p> \n <p style=\"line-height: 28pt\"><strong><span style=\"font-family: 方正黑体简体\"><span style=\"font-size: 16pt\">成果简介：</span></span></strong></p> \n <p align=\"center\" style=\"text-align: center; line-height: 28pt\"><strong><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">“重庆市主城区空气质量预报模型的研究与应用”成果简介</span></span></strong></p> \n <p style=\"line-height: 28pt\">&nbsp;</p> \n <p style=\"line-height: 28pt\"><strong><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">完成单位</span></span></strong><strong><span style=\"font-family: 宋体\"><span style=\"font-size: 14pt\">：&lt;/span&gt;</span></span></strong><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-size: 15pt\">重庆市环境监测中心</span></span></p> \n <p style=\"line-height: 28pt\"><strong><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">主要完成人员：</span></span></strong><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-size: 15pt\">罗财红、蒋昌潭、张&nbsp; 灿、刘姣姣、叶&nbsp; 堤、谯&nbsp; 捷、</span></span></p> \n <p style=\"line-height: 28pt; text-indent: 96.6pt\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-size: 15pt\">王启秀、黄&nbsp; 勇、李&nbsp; 灵、刘&nbsp; 伟、黄&nbsp; 伟</span></span></p> \n <p style=\"line-height: 28pt\"><strong><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">项目编号</span></span></strong><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-size: 15pt\">：环科字2015</span><span style=\"font-size: 20px\">第7</span></span><span style=\"font-size: 20px\">号</span></p> \n <p style=\"line-height: 28pt\">&nbsp;</p> \n <p style=\"line-height: 28pt; layout-grid-mode: char; margin-left: 27.9pt\"><strong><span style=\"font-family: 黑体\"><span style=\"font-size: 15pt\">一、研究背景</span></span></strong></p> \n <p style=\"line-height: 28pt; text-indent: 27.75pt; layout-grid-mode: char\"><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">国家《大气污染防治行动计划》（国发〔</span></span><span style=\"font-size: 15pt\">2013</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">〕</span></span><span style=\"font-size: 15pt\">37</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">号）和重庆市政府《贯彻落实大气污染防治行动计划的实施意见》（渝府发〔</span></span><span style=\"font-size: 15pt\">2013</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">〕</span></span><span style=\"font-size: 15pt\">86</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">号）均要求建设空气质量监测预警系统。并且对空气质量进行预报，及时发出预警，有利于促进管理部门及时采取针对性措施，达到降低污染的目的。</span></span></p> \n <p style=\"line-height: 28pt; layout-grid-mode: char; margin-left: 27.9pt\"><strong><span style=\"font-family: 黑体\"><span style=\"font-size: 15pt\">二、研究内容</span></span></strong></p> \n <p style=\"line-height: 28pt; text-indent: 27.75pt; layout-grid-mode: char\"><span style=\"font-size: 15pt\">1</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">．分析自</span></span><span style=\"font-size: 15pt\">2013</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">年以来，重庆市主城区空气中</span></span><span style=\"font-size: 15pt\">PM<sub>10</sub></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">、</span></span><span style=\"font-size: 15pt\">PM<sub>2.5</sub></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">、</span></span><span style=\"font-size: 15pt\">O<sub>3</sub></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">、、</span></span><span style=\"font-size: 15pt\">NO<sub>2</sub></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">、</span></span><span style=\"font-size: 15pt\">SO<sub>2</sub></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">、</span></span><span style=\"font-size: 15pt\">CO</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">等</span></span><span style=\"font-size: 15pt\">6</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">项污染物浓度的时空变化规律、特征，着重分析</span></span><span style=\"font-size: 15pt\">PM<sub>2.5</sub></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">、</span></span><span style=\"font-size: 15pt\">NO<sub>2</sub></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">、</span></span><span style=\"font-size: 15pt\">O<sub>3</sub></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">与温度、温差、太阳辐射、湿度、气压、气压差、水汽压、降雨、风速等</span></span><span style=\"font-size: 15pt\">9</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">项气象要素之间的关系。</span></span></p> \n <p style=\"line-height: 28pt; text-indent: 27.75pt; layout-grid-mode: char\"><span style=\"font-size: 15pt\">2</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">．采用主成分分析、逐步回归分析等统计学方法，筛选甄别出影响污染物浓度的显著气象要素因子，特别是针对重庆</span></span><span style=\"font-size: 15pt\">O<sub>3</sub></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">形成机理复杂、预报难度大的实际，研究影响重庆市主城区</span></span><span style=\"font-size: 15pt\">O<sub>3</sub></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">浓度变化的关键气象因子，为</span></span><span style=\"font-size: 15pt\">O<sub>3</sub></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">预报订正模型的开发奠定基础。</span></span></p> \n <p style=\"line-height: 28pt; text-indent: 27.75pt; layout-grid-mode: char\"><span style=\"font-size: 15pt\">3</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">．优化改进</span></span><span style=\"font-size: 15pt\">BP </span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">神经网络的算法，建立</span></span><span style=\"font-size: 15pt\">O<sub>3</sub></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">与主要气象条件的关系模型；并对模型进行开发，形成可视化、人机友好的预报预警平台软件。</span></span></p> \n <p style=\"line-height: 28pt; text-indent: 27.75pt; layout-grid-mode: char\"><span style=\"font-size: 15pt\">4</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">．将新模型的</span></span><span style=\"font-size: 15pt\">O<sub>3</sub></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">预报结果与</span></span><span style=\"font-size: 15pt\">2016</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">年主城区</span></span><span style=\"font-size: 15pt\">O<sub>3</sub></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">实际监测结果、原有模型的预报结果等进行比较分析，评估新模型的预报效果。</span></span></p> \n <p style=\"line-height: 28pt; text-indent: 27.9pt; layout-grid-mode: char\"><strong><span style=\"font-family: 黑体\"><span style=\"font-size: 15pt\">三、研究成果</span></span></strong></p> \n <p style=\"line-height: 28pt; text-indent: 27.75pt; layout-grid-mode: char\"><span style=\"font-size: 15pt\">1</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">．分析掌握了重庆市主城区</span></span><span style=\"font-size: 15pt\">PM<sub>2.5</sub></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">、</span></span><span style=\"font-size: 15pt\">O<sub>3</sub></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">等污染物精细的时空变化规律。</span></span></p> \n <p style=\"line-height: 28pt; text-indent: 27.75pt; layout-grid-mode: char\"><span style=\"font-size: 15pt\">2</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">．首次发现气压差、温差、太阳辐射与气温等关键因素的组合是影响重庆主城区臭氧生成的决定性气象因素。</span></span></p> \n <p style=\"line-height: 28pt; text-indent: 27.75pt; layout-grid-mode: char\"><span style=\"font-size: 15pt\">3</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">．开发了适宜于重庆本地高温高湿环境条件下的</span></span><span style=\"font-size: 15pt\">O<sub>3</sub></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">预报模型。经评估，开发的</span></span><span style=\"font-size: 15pt\">O<sub>3</sub></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">模型</span></span><span style=\"font-size: 15pt\">24h</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">预报对</span></span><span style=\"font-size: 15pt\">O<sub>3</sub></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">的类别预报准确率为</span></span><span style=\"font-size: 15pt\">74.8%</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">，较原模型提高</span></span><span style=\"font-size: 15pt\">7.3</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">个百分点；对</span></span><span style=\"font-size: 15pt\">O<sub>3</sub></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">首要污染物预报准确率为</span></span><span style=\"font-size: 15pt\">71.2%</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">，较原模型提高了</span></span><span style=\"font-size: 15pt\">10</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">个百分点，使我市的臭氧污染预报准确率达到国内先进水平。</span></span></p> \n <p style=\"line-height: 28pt; text-indent: 27.75pt; layout-grid-mode: char\"><span style=\"font-size: 15pt\">4</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">．</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">开发的</span></span><span style=\"font-size: 15pt\">O<sub>3</sub></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">订正模块已经接入重庆市预报预警决策支持系统，模型已用于重庆市空气质量预报工作。</span></span></p> \n <p style=\"line-height: 28pt; text-indent: 27.9pt; layout-grid-mode: char\">&nbsp;</p> \n <p style=\"line-height: 28pt; text-indent: 27.9pt; layout-grid-mode: char\">&nbsp;</p> \n <p style=\"line-height: 28pt\">&nbsp;</p> \n <p style=\"line-height: 28pt\">&nbsp;</p> \n <p style=\"line-height: 28pt\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-size: 15pt\">送:</span>局领导，机关各处室，局属各单位，市级有关部门，有关院校&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&nbsp;&nbsp;&nbsp;<span style=\"font-family: 仿宋_gb2312\"><span style=\"font-size: 15pt\">编辑：&nbsp; 陈&nbsp; 敏&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 电话：88733521&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>传真：89181803</span></p> \n <p style=\"line-height: 28pt\"><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-size: 15pt\">审核：&nbsp; 张&nbsp; 懿&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; E-mail</span>：cqhbkjc@163.com</span></p> \n <p style=\"line-height: 28pt\">&nbsp;</p> \n <p>&nbsp;</p> \n <p></p> \n <!--endprint--> \n <!--<$[CONTENT]>end--> \n</div>', '重庆市环保规划科技动态2017年1月', '2017-02-14 00:00:00', 'http://www.cepb.gov.cn/doc/2017/02/14/153459.shtml');
INSERT INTO `site_info` VALUES ('117', '<h1>\n <!--<$[TITLE]>start-->重庆市环保规划科技动态第9期\n <!--<$[TITLE]>end--></h1><div id=\"Zoom\"> \n <!--<$[CONTENT]>start--> \n <p></p>\n <p><strong><span style=\"letter-spacing: -1pt\"><span style=\"font-family: 宋体\"><span style=\"color: red\"><span style=\"font-size: 42pt\">重庆市环保规划科技动态</span></span></span></span></strong></p> \n <table cellpadding=\"0\" cellspacing=\"0\" width=\"100%\"> \n  <tbody> \n   <tr> \n    <td> \n     <div style=\"padding-right: 7.2pt; padding-left: 7.2pt; padding-bottom: 3.6pt; padding-top: 3.6pt\"> \n      <p>&nbsp;</p> \n      <p>&nbsp;</p> \n      <p><span style=\"z-index: 1; left: 0pt; position: absolute\"><span style=\"font-family: 楷体_gb2312\">市环保局规划科技处</span></span></p> \n      <p>&nbsp;</p> \n     </div> </td> \n   </tr> \n  </tbody> \n </table> \n <table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" style=\"border-collapse: collapse\"> \n  <tbody> \n   <tr style=\"height: 75px\"> \n    <td style=\"padding-right: 5.4pt; padding-left: 5.4pt; padding-bottom: 0cm; width: 57px; padding-top: 0cm; height: 75px\"> <p><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">主编：</span></span></p> </td> \n    <td style=\"padding-right: 5.4pt; padding-left: 5.4pt; padding-bottom: 0cm; width: 159px; padding-top: 0cm; height: 75px\"> <p><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">市环保局规划科技处</span></span></p> <p><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">市环保局总工办</span></span></p> </td> \n    <td style=\"padding-right: 5.4pt; padding-left: 5.4pt; padding-bottom: 0cm; width: 219px; padding-top: 0cm; height: 75px\"> <p align=\"center\" style=\"text-align: center\"><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">2016</span></span><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">年第9</span>期(</span>共67期)</p> </td> \n    <td style=\"padding-right: 5.4pt; padding-left: 5.4pt; padding-bottom: 0cm; width: 149px; padding-top: 0cm; height: 75px\"> <p align=\"right\" style=\"margin-right: 16.35pt; text-align: right\"><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">2016 </span></span><span style=\"font-family: 楷体_gb2312\"><span style=\"font-size: 12pt\">年 9</span>月</span></p> </td> \n   </tr> \n  </tbody> \n </table> \n <p>&nbsp;</p> \n <p style=\"line-height: 28pt\"><strong><span style=\"font-family: 方正黑体简体\"><span style=\"font-size: 16pt\">本期导读:</span></span></strong></p> \n <p style=\"margin-left: 48.6pt; text-indent: -22.45pt; line-height: 28pt\"><span style=\"letter-spacing: 0.5pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">1</span></span></span><span style=\"letter-spacing: 0.5pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">．重庆市生态保护红线划定方案报市政府审议</span></span></span></p> \n <p style=\"margin-left: 48.6pt; text-indent: -22.45pt; line-height: 28pt\"><span style=\"letter-spacing: 0.5pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">2</span></span></span><span style=\"letter-spacing: 0.5pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">．市环保局建立环保规划管理工作协调机制</span></span></span></p> \n <p style=\"margin-left: 48.6pt; text-indent: -22.45pt; line-height: 28pt\"><span style=\"letter-spacing: 0.5pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">3</span></span></span><span style=\"letter-spacing: 0.5pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">．重庆三峰环境集团成为首家布局丝绸之路经济带的国内垃圾发电企业</span></span></span></p> \n <p style=\"margin-left: 48.6pt; text-indent: -22.45pt; line-height: 28pt\"><span style=\"letter-spacing: 0.5pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">4</span></span></span><span style=\"letter-spacing: 0.5pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">．好氧颗粒污泥污水处理新技术应用在我市取得突破</span></span></span></p> \n <p style=\"margin-left: 48.6pt; text-indent: -22.45pt; line-height: 28pt\"><span style=\"letter-spacing: 0.5pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">5</span></span></span><span style=\"letter-spacing: 0.5pt\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">．膜法智能污水处理器填补了我市环保膜产品空白</span></span></span></p> \n <pre style=\"line-height: 28pt\">\r\n&nbsp;</pre> \n <p style=\"line-height: 28pt\"><strong><span style=\"font-family: 方正黑体简体\"><span style=\"font-size: 16pt\">成果简介：</span></span></strong></p> \n <p align=\"center\" style=\"text-align: center\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">基于沉水植物的复合水体生态重建技术在重庆的应用示范</span></span></p> \n <p align=\"center\" style=\"text-align: center\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">成果简介</span></span></p> \n <pre style=\"line-height: 30pt\">\r\n&nbsp;</pre> \n <p style=\"line-height: 30pt\"><strong><span style=\"font-family: 方正黑体简体\"><span style=\"font-size: 16pt\">简&nbsp;&nbsp; 讯：</span></span></strong></p> \n <p style=\"text-indent: 36.9pt\"><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">1</span></span><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">．【重庆市生态保护红线划定方案报市政府审议】</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">划定生态保护红线是中央和市委确定的深化生态文明体制改革的一项重要内容。我市按照国家发展改革委等九部委《关于加强资源环境生态红线管控的指导意见》、环保部《生态保护红线划定技术指南》等要求，历经前期研究、组织起草、征求意见、衔接平衡、专家论证等程序，多次召集市级部门、区县和有关专家共同研究，对各方提出的意见建议进行充分吸纳，目前，已形成了《重庆市生态保护红线划定方案（送审稿）》，正报请市政府审议。</span></span></p> \n <p style=\"text-indent: 36.9pt\"><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">2</span></span><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">．【市环保局建立环保规划管理工作协调机制】</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">近日，为进一步理顺环保规划管理工作，强化各项规划涉及环保领域的管理要求，保障规划实施的可操作性和时效性，市环保局研究建立了环保规划管理的分工协作机制和信息共享机制，明确了各相关规划管理工作的牵头业务处室和单位，并要求相关信息应及时通过书面方式或办公系统在内部进行通报。随即，按工作进度，通报了万州区、南川区、垫江县、巫溪县等城乡总体规划审查相关信息。</span></span></p> \n <p style=\"text-indent: 36.9pt\"><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">3. </span></span><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">【重庆三峰环境集团成为首家布局丝绸之路经济带的国内垃圾发电企业】</span></span> <span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">近日，在第三届中国—亚欧博览会上重庆三峰环境集团分别与新疆维吾尔自治区库尔勒市和阿克苏地区政府签订了生活垃圾焚烧发电</span></span><span style=\"font-size: 16pt\">PPP</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">项目，成为我国首家布局丝绸之路经济带的垃圾发电企业。项目将强力助推我市环保产业“走出去”，带动环保装备相关产业的发展。</span></span></p> \n <p style=\"text-indent: 36.9pt\"><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">4. </span></span><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">【好氧颗粒污泥污水处理新技术应用在我市取得突破】</span></span> <span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">近日，由重庆市环境科学研究院提供技术支撑的好氧颗粒污泥污水处理新技术应用在我市取得突破，其应用于荣昌区直升镇污水处理厂的改造工程取得成功。改造前，处理出水难以达到国家《城镇污水处理厂污染物排放标准》一级</span></span><span style=\"font-size: 16pt\">B</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">标准；改造后，出水稳定达到一级</span></span><span style=\"font-size: 16pt\">B</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">标准要求，且</span></span><span style=\"font-size: 16pt\">COD</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">、氨氮、总氮浓度等指标优于一级</span></span><span style=\"font-size: 16pt\">A</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">标准。该技术水质水量波动适应能力强，适宜我市城镇生活污水处理的实际。</span></span></p> \n <p style=\"text-indent: 36.9pt\"><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">5</span></span><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">．【膜法智能污水处理器填补了我市环保膜产品空白】</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">近日，由中国科学院重庆绿色智能技术研究院提供技术支撑的“中科禹浜</span></span><span style=\"font-size: 16pt\">YB-SMBR</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 16pt\">膜法智能污水处理器”在我市村镇污水处理的示范工程项目已完成验收。该处理器集成高性能膜材料技术与生物强化技术，降解污水中的污染成分，达到排放或回用要求，具有占地面积小、出水水质好、自动化程度高等优点<a name=\"_GoBack\"></a>。同时，该产品填补了我市环保膜产品空白，丰富了我市环保装备类型。</span></span></p> \n <p style=\"text-indent: 36.9pt\">&nbsp;</p> \n <p style=\"text-indent: 36.9pt\">&nbsp;</p> \n <p style=\"text-indent: 36.9pt\">&nbsp;</p> \n <p style=\"text-indent: 39.7pt; line-height: 30pt\">&nbsp;</p> \n <p style=\"text-indent: 39.7pt; line-height: 30pt\">&nbsp;</p> \n <p style=\"text-indent: 39.7pt; line-height: 30pt\">&nbsp;</p> \n <p><br clear=\"all\"> &nbsp;</p> \n <p style=\"line-height: 30pt\"><strong><span style=\"font-family: 方正黑体简体\"><span style=\"font-size: 16pt\">成果简介：</span></span></strong></p> \n <p align=\"center\" style=\"text-align: center\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">基于沉水植物的复合水体生态重建技术在重庆的应用示范</span></span></p> \n <p align=\"center\" style=\"text-align: center\"><span style=\"font-family: 方正楷体简体\"><span style=\"font-size: 16pt\">成果简介</span></span></p> \n <p align=\"left\" style=\"line-height: 30pt; text-align: left\">&nbsp;</p> \n <p><strong><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">完成单位：</span></span></strong><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">重庆屹唯新清洁技术有限公司</span></span></p> \n <p style=\"margin-left: 88.1pt; text-indent: -88.1pt\"><strong><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">主要完成人</span></span></strong><strong><span style=\"font-family: 宋体\">：</span></strong><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">陈迅、刘少武、王雪峰、李彦婷、吴正松、王少杰、揭起武</span></span></p> \n <p><strong><span style=\"font-family: 方正小标宋简体\"><span style=\"font-size: 15pt\">项目编号：</span></span></strong><span style=\"font-family: 方正仿宋简体\"><span style=\"font-size: 16pt\">环科字2014</span>第21</span>号</p> \n <p style=\"text-justify: inter-ideograph; layout-grid-mode: char; text-indent: 29.25pt; line-height: 28pt; text-align: justify\">&nbsp;</p> \n <p style=\"layout-grid-mode: char; text-indent: 32.1pt; line-height: 28pt\"><span style=\"font-family: 黑体\"><span style=\"font-size: 15pt\">一、技术原理</span></span></p> \n <p style=\"layout-grid-mode: char; text-indent: 29.85pt; line-height: 28pt\"><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">在城市河流或浅水湖泊中建立以适宜沉水植物及水生动物为主的水生态系统，利用沉水植物的生长及光合作用去除水体及底泥中过多营养物质，利用水生动物防止沉水植物过分生长，从而形成平衡的水下生态系统，达到在无需清淤的条件下，水体长期保持清澈透明、水质良好的效果。</span></span></p> \n <p style=\"layout-grid-mode: char; text-indent: 32.1pt; line-height: 28pt\"><span style=\"font-family: 黑体\"><span style=\"font-size: 15pt\">二、技术示范应用的基本情况</span></span></p> \n <p align=\"left\" style=\"layout-grid-mode: char; text-indent: 29.85pt; line-height: 28pt; text-align: left\"><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">示范项目位于两江新区龙咀公园内，在丰收水库库尾选择约</span></span><span style=\"font-size: 15pt\">3000m<sup>2</sup></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">的独立水域建立水生态系统。</span></span></p> \n <p style=\"layout-grid-mode: char; text-indent: 32.1pt; line-height: 28pt\"><span style=\"font-family: 黑体\"><span style=\"font-size: 15pt\">三、实施过程</span></span></p> \n <p align=\"left\" style=\"layout-grid-mode: char; text-indent: 29.85pt; line-height: 28pt; text-align: left\"><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">实施过程主要为前期调研</span></span><span style=\"font-size: 15pt\">——</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">方案设计</span></span><span style=\"font-size: 15pt\">——</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">底泥处置</span></span><span style=\"font-size: 15pt\">——</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">不同品种沉水植物试种</span></span><span style=\"font-size: 15pt\">——</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">观察沉水植物生长情况</span></span><span style=\"font-size: 15pt\">——</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">控制水位确保沉水植物成活</span></span><span style=\"font-size: 15pt\">——</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">逐步提高水位</span></span><span style=\"font-size: 15pt\">——</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">逐步投放大型鱼虾贝类</span></span><span style=\"font-size: 15pt\">——</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">养护。</span></span></p> \n <p style=\"layout-grid-mode: char; text-indent: 32.1pt; line-height: 28pt\"><span style=\"font-family: 黑体\"><span style=\"font-size: 15pt\">四、实施效果</span></span></p> \n <p align=\"left\" style=\"layout-grid-mode: char; text-indent: 29.85pt; line-height: 28pt; text-align: left\"><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">沉水植物栽种后，经历短暂的适应后，开始迅速生长，</span></span><span style=\"font-size: 15pt\">40</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">天左右，基本成片，</span></span><span style=\"font-size: 15pt\">2</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">个月后，几乎覆盖全部水下面积。沉水植物全面栽种后约三周，水体透明度达到</span></span><span style=\"font-size: 15pt\">1.2-1.5<span style=\"font-family: 方正仿宋_gbk\">米</span></span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">，水体水质主要指标（氨氮、总氮、总磷、</span></span><span style=\"font-size: 15pt\">COD</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">、溶解氧等）达到地表水Ⅲ类标准，部分指标达到Ⅰ类标准。</span></span></p> \n <p style=\"layout-grid-mode: char; text-indent: 32.1pt; line-height: 28pt\"><span style=\"font-family: 黑体\"><span style=\"font-size: 15pt\">五、技术适用条件</span></span></p> \n <p align=\"left\" style=\"layout-grid-mode: char; text-indent: 29.85pt; line-height: 28pt; text-align: left\"><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">该技术较适合应用于外源污染物已基本截流完毕、水深不宜超过</span></span><span style=\"font-size: 15pt\">3m</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">或超过</span></span><span style=\"font-size: 15pt\">3m</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">的水域面积小于全水域面积</span></span><span style=\"font-size: 15pt\">40%</span><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">的浅水湖泊和独立浅水静水区域。</span></span></p> \n <p style=\"layout-grid-mode: char; text-indent: 32.1pt; line-height: 28pt\"><span style=\"font-family: 黑体\"><span style=\"font-size: 15pt\">六、维护管理</span></span></p> \n <p align=\"left\" style=\"layout-grid-mode: char; text-indent: 29.85pt; line-height: 28pt; text-align: left\"><span style=\"font-family: 方正仿宋_gbk\"><span style=\"font-size: 15pt\">重建的水生态需要精心的维护，内容包括：控制有害生物的数量，控制沉水植物的高度，控制鱼类的品种和数量等。</span></span></p> \n <p align=\"left\" style=\"layout-grid-mode: char; text-indent: 29.85pt; line-height: 28pt; text-align: left\">&nbsp;</p> \n <p align=\"left\" style=\"layout-grid-mode: char; text-indent: 29.85pt; line-height: 28pt; text-align: left\">&nbsp;</p> \n <p align=\"left\" style=\"layout-grid-mode: char; text-indent: 29.85pt; line-height: 28pt; text-align: left\">&nbsp;</p> \n <p align=\"left\" style=\"layout-grid-mode: char; text-indent: 29.85pt; line-height: 28pt; text-align: left\">&nbsp;</p> \n <p align=\"left\" style=\"layout-grid-mode: char; text-indent: 29.85pt; line-height: 28pt; text-align: left\">&nbsp;</p> \n <p align=\"left\" style=\"layout-grid-mode: char; text-indent: 29.85pt; line-height: 28pt; text-align: left\">&nbsp;</p> \n <p style=\"layout-grid-mode: char; line-height: 28pt\">&nbsp;</p> \n <p><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-size: 15pt\">送:</span>局领导，机关各处室，局属各单位，市级有关部门，有关院校&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&nbsp;&nbsp;&nbsp;<span style=\"font-family: 仿宋_gb2312\"><span style=\"font-size: 15pt\">编辑：&nbsp; 陈&nbsp; 敏&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 电话：88733521&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>传真：89181803</span></p> \n <p><span style=\"font-family: 仿宋_gb2312\"><span style=\"font-size: 15pt\">审核：&nbsp; 张&nbsp; 懿&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; E-mail</span>：cqhbkjc@163.com</span></p> \n <p></p> \n <!--endprint--> \n <!--<$[CONTENT]>end--> \n</div>', '重庆市环保规划科技动态第9期', '2016-12-07 00:00:00', 'http://www.cepb.gov.cn/doc/2016/12/07/149208.shtml');
INSERT INTO `site_info` VALUES ('118', '<h1>\n <!--<$[TITLE]>start-->重庆市长江三峡水库库区及流域水污染防治条例\n <!--<$[TITLE]>end--></h1><div id=\"Zoom\"> \n <!--<$[CONTENT]>start--> \n <p></p>\n <p>&nbsp;</p> \n <p align=\"center\" class=\"MsoNormal\" style=\"TEXT-INDENT: 23.6pt; LINE-HEIGHT: 150%; TEXT-ALIGN: center; mso-char-indent-count: 2.0\"><span style=\"FONT-SIZE: 12pt; LINE-HEIGHT: 150%; FONT-FAMILY: 宋体\">重庆市人民代表大会常务委员会公告<span lang=\"EN-US\">\n    <!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /-->\n    <o:p></o:p></span></span></p> \n <p align=\"center\" class=\"MsoNormal\" style=\"TEXT-INDENT: 23.6pt; LINE-HEIGHT: 150%; TEXT-ALIGN: center; mso-char-indent-count: 2.0\"><span style=\"FONT-SIZE: 12pt; LINE-HEIGHT: 150%; FONT-FAMILY: 宋体\">〔<span lang=\"EN-US\">2011</span>〕<span lang=\"EN-US\">26</span>号<span lang=\"EN-US\">\n    <o:p></o:p></span></span></p> \n <p class=\"MsoNormal\" style=\"TEXT-INDENT: 23.6pt; LINE-HEIGHT: 150%; mso-char-indent-count: 2.0\"><span lang=\"EN-US\" style=\"FONT-SIZE: 12pt; LINE-HEIGHT: 150%; FONT-FAMILY: 宋体\">\n   <o:p>\n    &nbsp;\n   </o:p></span></p> \n <p class=\"MsoNormal\" style=\"TEXT-INDENT: 23.6pt; LINE-HEIGHT: 150%; mso-char-indent-count: 2.0\"><span style=\"FONT-SIZE: 12pt; LINE-HEIGHT: 150%; FONT-FAMILY: 宋体\">《重庆市长江三峡水库库区及流域水污染防治条例》已于\n   <!--?xml:namespace prefix = st1 ns = \"urn:schemas-microsoft-com:office:smarttags\" /-->\n   <st1:chsdate day=\"29\" islunardate=\"False\" isrocdate=\"False\" month=\"7\" w:st=\"on\" year=\"2011\">\n    <span lang=\"EN-US\">2011</span>年\n    <span lang=\"EN-US\">7</span>月\n    <span lang=\"EN-US\">29</span>日\n   </st1:chsdate>经重庆市第三届人民代表大会常务委员会第二十五次会议审议通过，现予公布，自\n   <st1:chsdate day=\"1\" islunardate=\"False\" isrocdate=\"False\" month=\"10\" w:st=\"on\" year=\"2011\">\n    <span lang=\"EN-US\">2011</span>年\n    <span lang=\"EN-US\">10</span>月\n    <span lang=\"EN-US\">1</span>日起\n   </st1:chsdate>施行。<span lang=\"EN-US\">\n    <o:p></o:p></span></span></p> \n <p class=