INSERT INTO `site_info` VALUES ('162', '<h1>\n <!--<$[TITLE]>start-->中共重庆市环境保护局党组关于米金套同志任职的通知\n <!--<$[TITLE]>end--></h1><div id=\"Zoom\"> \n <!--<$[CONTENT]>start--> \n <p></p>\n <p style=\"text-align: right\"><span style=\"font-family: 宋体\"><span style=\"font-size: 16px\"><a name=\"gwz\">渝环党</a>〔<a name=\"gwnh\">2018</a>〕<a name=\"gwqh\">4</a>号</span></span></p> \n <p align=\"center\" style=\"text-align: center; line-height: 27.5pt\"><span style=\"font-size: 18px\"><strong><span style=\"font-family: 宋体\"><a name=\"tbx_bt\">中共重庆市环境保护局党组</a></span></strong></span></p> \n <p align=\"center\" style=\"text-align: center; line-height: 27.5pt\"><span style=\"font-size: 18px\"><strong><span style=\"font-family: 宋体\">关于米金套同志任职的通知</span></strong></span></p> \n <p style=\"line-height: 27.5pt\"><span style=\"font-family: 宋体\"><span style=\"font-size: 16px\">&nbsp;</span></span></p> \n <p style=\"line-height: 27.5pt\"><span style=\"font-family: 宋体\"><span style=\"font-size: 16px\"><a name=\"zsdw\">各区县（自治县）、万盛经开区环保局，驻市环保局纪检组，市环保局各处室、分局，直属单位，监察办，环投公司</a>：</span></span></p> \n <p style=\"line-height: 27.5pt; text-indent: 32pt\"><span style=\"font-family: 宋体\"><span style=\"font-size: 16px\">接市委组织部渝组干〔2017〕583号文件通知，市委组织部研究批准：</span></span></p> \n <p style=\"line-height: 27.5pt; text-indent: 32pt\"><span style=\"font-family: 宋体\"><span style=\"font-size: 16px\">米金套同志任中共重庆市环境保护局党组成员、重庆市环境保护局局长助理（挂职1年，人事关系和职级不变）。</span></span></p> \n <p style=\"line-height: 27.5pt; text-indent: 32pt\"><span style=\"font-family: 宋体\"><span style=\"font-size: 16px\">&nbsp;</span></span></p> \n <p style=\"line-height: 27.5pt; text-indent: 32pt\"><span style=\"font-family: 宋体\"><span style=\"font-size: 16px\">&nbsp;</span></span></p> \n <p style=\"line-height: 27.5pt; text-indent: 32pt\"><span style=\"font-family: 宋体\"><span style=\"font-size: 16px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 中共重庆市环境保护局党组</span></span></p> \n <p style=\"li