INSERT INTO `site_info` VALUES ('163', '<h1>\n <!--<$[TITLE]>start-->中共重庆市环境保护局党组关于杨中亮同志职务任免的通知\n <!--<$[TITLE]>end--></h1><div id=\"Zoom\"> \n <!--<$[CONTENT]>start--> \n <p></p>\n <p style=\"line-height: 28.5pt\"><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">机关各处室、各分局，局属各单位：</span></span></p> \n <p style=\"line-height: 28.5pt; text-indent: 32pt\"><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">2017</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">年</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">1</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">月</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">22</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">日</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">，经局党组会议研究决定：</span></span></p> \n <p style=\"line-height: 28.5pt; text-indent: 32pt\"><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">杨中亮同志任重庆市环境监察办公室监察一处处长（试用期</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">1</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">年），免去重庆市环境保护局办公室副主任职务。</span></span></p> \n <p style=\"line-height: 28.5pt; text-indent: 32pt\"><span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">特此通知。</span></span></p> \n <p style=\"line-height: 30pt\">&nbsp;</p> \n <p style=\"line-height: 30pt\">&nbsp;</p> \n <p style=\"line-height: 30pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-size: 16pt\"><span style=\"font-family: 方正仿宋_gbk\">中共重庆市环境保护局党组</span></span></p> \n <p><span style=\"font-size: 16pt\"><span style=\"font-family: times new roman\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&