INSERT INTO `site_info` VALUES ('174', '<h1>\n <!--<$[TITLE]>start-->中共重庆市环境保护局党组关于王勤等5名同志职务任免的通知\n <!--<$[TITLE]>end--></h1><div id=\"Zoom\"> \n <!--<$[CONTENT]>start--> \n <p> <style>\r\n/* Generator: eWebEditor */\r\np.MsoNormal, li.MsoNormal, div.MsoNormal {margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\"Times New Roman\";}\r\np.CharCharChar, li.CharCharChar, div.CharCharChar {margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:12.0pt;font-family:Tahoma;}\r\ndiv.Section1 {page:Section1;}\r\n</style> </p>\n <p style=\"LINE-HEIGHT: 150%\" class=\"MsoNormal\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt\">机关各处室，各分局，各直属单位：</span></p> \n <p style=\"LINE-HEIGHT: 150%; TEXT-INDENT: 24pt\" class=\"MsoNormal\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt\">2014</span><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt\">年<span>5</span>月<span>23</span>日</span><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt\">，经局党组会议研究，决定：</span></p> \n <p style=\"LINE-HEIGHT: 150%; TEXT-INDENT: 24pt\" class=\"MsoNormal\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt\">王勤同志任市环境保护局两江新区分局党组书记、副局长，免去市环境保护局北部新区分局局长职务；</span></p> \n <p style=\"LINE-HEIGHT: 150%; TEXT-INDENT: 24pt\" class=\"MsoNormal\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt\">王兴成同志任市环境保护局两江新区分局局长、党组副书记，免去原市环境保护局两江新区分局局长职务；</span></p> \n <p style=\"LINE-HEIGHT: 150%; TEXT-INDENT: 24pt\" class=\"MsoNormal\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt\">蒋良维同志任市环境保护局两江新区分局副局长、党组成员，免去市环境保护局北部新区分局副局长职务；</span></p> \n <p style=\"LINE-HEIGHT: 150%; TEXT-INDENT: 24pt\" class=\"MsoNormal\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt\">雷学良同志任市环境保护局两江新区分局副局长、党组成员，免去市环境保护局北部新区分局副局长职务；</span></p> \n <p style=\"LINE-HEIGHT: 150%; TEXT-INDENT: 24pt\" class=\"MsoNormal\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt\">吴忠同志任市环境保护局两江新区分局副局长，免去原市环境保护局两江新区分局副局长职务。</span></p> \n <p style=\"LINE-HEIGHT: 150%; TEXT-INDENT: 24pt\" class=\"MsoNormal\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt\">特此通知。</span></p> \n <p style=\"LINE-HEIGHT: 150%; TEXT-INDENT: 24pt\" class=\"MsoNormal\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></p> \n <p style=\"LINE-HEIGHT: 150%; TEXT-INDENT: 24pt\" class=\"MsoNormal\"><span style=\"LINE-HEIGHT: 150%; FONT-FAMILY: 宋体; FONT-SIZE: 12pt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span>&nbsp;</span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>2014<span><span>年5</span></span><span><span>月28</span></span><span><span>日</span></span></span></p>\n <p></p> \n <!--endprint--> \n <!--<$[CONTENT]>end--> \n</div>', '中共重庆市环境保护局党组关于王勤等5名同志职务任免的通知', '2014-05-28 00:00:00', 'http://www.cepb.gov.cn/doc/2014/05/28/30328.shtml');
