INSERT INTO `site_info` VALUES ('187', '<h1>\n <!--<$[TITLE]>start-->环保部启动多个重点专项规划实施情况中期评估\n <!--<$[TITLE]>end--></h1><div id=\"Zoom\"> \n <!--<$[CONTENT]>start--> \n <p></p>\n <div>\n  2013年4月以来，环保部陆续发出通知，有步骤地启动了《“十二五”危险废物污染防治规划》、《全国城市饮用水源地环境保护规划》、《全国主要行业持久性有机污染物污染防治“十二五”规划》等重点专项规划的评估工作，要求各省（自治区、直辖市）按照规定时限完成自评，客观评价实施情况，明确下一步《规划》实施重点，提出完善建议。\n </div> \n <p></p> \n <!--endprint--> \n <!--<$[CONTENT]>end--> \n</div>', '环保部启动多个重点专项规划实施情况中期评估', '2013-09-20 00:00:00', 'http://www.cepb.gov.cn/doc/2013/09/20/68648.shtml');
INSERT INTO `site_info` VALUES ('188', '<h1>\n <!--<$[TITLE]>start-->我局完成国民经济和社会发展相关“十二五”规划环境保护专项中期评估工作\n <!--<$[TITLE]>end--></h1><div id=\"Zoom\"> \n <!--<$[CONTENT]>start--> \n <p></p>\n <div>\n  根据市政府工作部署，市发展改革委牵头启动了《重庆市国民经济和社会发展“十二五”规划》、《主城区区二环区域发展规划》、《成渝经济区区域规划》等多个重点规划的中期评估工作，其中均要求对涉及到的环境保护目标任务进展情况进行评估。经认真评估，所涉各项环境保护目标任务、重点工程措施进展顺利，实现“时间过半、任务过半”的目标。\n </div> \n <p></p> \n <!--endprint--> \n <!--<$[CONTENT]>end--> \n</div>', '我局完成国民经济和社会发展相关“十二五”规划环境保护专项中期评估工作', '2013-09-05 00:00:00', 'http://www.cepb.gov.cn/doc/2013/09/05/68647.shtml');
INSERT INTO `site_info` VALUES ('189', '<h1>\n <!--<$[TITLE]>start-->重庆市环境保护局关于印发重庆市“十二五”机动车排气污染防治管理工作规划的通知\n <!--<$[TITLE]>end--></h1><div id=\"Zoom\"> \n <!--<$[CONTENT]>start--> \n <p><style type=\"text/css\">/* Generator: eWebEditor */\np.MsoNormal, li.MsoNormal, div.MsoNormal {margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:16.0pt;font-family:\"Times New Roman\";}\np.CharCharChar, li.CharCharChar, div.CharCharChar {margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:12.0pt;font-family:Tahoma;}\ndiv.Section1 {page:Section1;}\n</style> </p>\n <p align=\"left\" class=\"MsoNormal\" style=\"TEXT-ALIGN: left; LINE-HEIGHT: 30pt; TEXT-INDENT: -88.35pt; MARGIN-LEFT: 88.35pt\">&nbsp;</p> \n <p class=\"MsoNormal\" style=\"LINE-HEIGHT: 28pt\"><span style=\"FONT-FAMILY: 方正仿宋简体; FONT-SIZE: 16.5pt\">各区县（自治县）环保局，万州、万盛、双桥经开区环保局，市环保局北部新区、两江新区、长寿经开区分局，机关各处室、局属各单位：</span></p> \n <p class=\"MsoNormal\" style=\"LINE-HEIGHT: 28pt; TEXT-INDENT: 33pt\"><span style=\"FONT-FAMILY: 方正仿宋简体; FONT-SIZE: 16.5pt\">我局<span style=\"LETTER-SPACING: -0.2pt\">组织编制的《重庆市</span></span><span style=\"LETTER-SPACING: -0.2pt; FONT-SIZE: 16.5pt\">“</span><span style=\"LETTER-SPACING: -0.2pt; FONT-FAMILY: 方正仿宋简体; FONT-SIZE: 16.5pt\">十二五</span><span style=\"LETTER-SPACING: -0.2pt; FONT-SIZE: 16.5pt\">”</span><span style=\"LETTER-SPACING: -0.2pt; FONT-FAMILY: 方正仿宋简体; FONT-SIZE: 16.5pt\">机动车排气污染防治管理工作规划》已经局务会审议通过，现印发给你们，请遵照执行。</span></p> \n <p class=\"MsoNormal\" style=\"LINE-HEIGHT: 28pt; TEXT-INDENT: -49.5pt; MARGIN-LEFT: 86.8pt\">&nbsp;</p> \n <p class=\"MsoNormal\" style=\"LINE-HEIGHT: 28pt; TEXT-INDENT: 33pt\"><span style=\"FONT-FAMILY: 方正仿宋简体; COLOR: black; FONT-SIZE: 16.5pt\">附件：<a href=\"/uploadfiles/201209/04/2012090409340086032825.doc\" target=\"_blank\">重庆市“十二五”机动车排气污染物防治管理工作规划.doc</a></span></p> \n <p align=\"right\" class=\"MsoNormal\" style=\"TEXT-ALIGN: right; LINE-HEIGHT: 30pt; MARGIN-RIGHT: 31.6pt\">&nbsp;</p> \n <p align=\"right\" class=\"MsoNormal\" style=\"TEXT-ALIGN: right; LINE-HEIGHT: 30pt; MARGIN-RIGHT: 64.6pt\"><span style=\"FONT-FAMILY: 方正仿宋简体; COLOR: black; FONT-SIZE: 16.5pt\">重庆市环境保护局</span></p> \n <p><span style=\"FONT-FAMILY: \'Times New Roman\'; COLOR: black; FONT-SIZE: 16.5pt\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>2012<span style=\"FONT-FAMILY: 方正仿宋简体\"><span>年</span></span>9<span style=\"FONT-FAMILY: 方正仿宋简体\"><span>月</span></span>4<span style=\"FONT-FAMILY: 方正仿宋简体\"><span>日</span></span></span></p> \n <p>&nbsp;</p> \n <p></p> \n <!--endprint--> \n <!--<$[CONTENT]>end--> \n</div>', '重庆市环境保护局关于印发重庆市“十二五”机动车排气污染防治管理工作规划的通知', '2012-09-04 00:00:00', 'http://www.cepb.gov.cn/doc/2012/09/04/68646.shtml');
