INSERT INTO `site_info` VALUES ('198', '<h1>\n <!--<$[TITLE]>start-->第三批主城区及周边区县大气污染源被通报\n <!--<$[TITLE]>end--></h1><div id=\"Zoom\"> \n <!--<$[CONTENT]>start--> \n <p></p>\n <div> \n  <p align=\"center\" style=\"text-align: center; line-height: 35pt; layout-grid-mode: char\">&nbsp;</p> \n  <p style=\"line-height: 28pt; text-indent: 32pt; layout-grid-mode: char\"><span style=\"font-size: 16pt\"><span style=\"font-family: 宋体\">9</span></span><span style=\"font-size: 16pt\"><span style=\"font-family: 宋体\">月1</span>日</span><span style=\"font-size: 16pt\"><span style=\"font-family: 宋体\">以来，我市主城区空气质量连续出现轻度污染，部分区达到中度污染。经市政府蓝天行动督查组近期在主城区及周边区域开展现场巡查，发现江北区石马河街道轨道交通环线工地等12</span>个大气污染源问题较为突出，予以公开通报（见附件）。</span></p> \n  <p style=\"line-height: 28pt; text-indent: 32pt; layout-grid-mode: char\"><span style=\"font-size: 16pt\"><span style=\"font-family: 宋体\">市政府蓝天行动督查组强调，各区人民政府和市政府有关部门务必高度重视大气污染防治工作，加强所管辖范围的交通污染、扬尘污染、工业污染、生活污染等大气污染的控制。</span></span></p> \n  <p style=\"line-height: 28pt; text-indent: 32pt; layout-grid-mode: char\"><span style=\"font-size: 16pt\"><span style=\"font-family: 宋体\">对于本次被通报的大气污染源，市政府蓝天行动督查组要求，由主管部门依法查处并责令有关单位立即整改，将处理和整改情况于9月9日前书面回复督查组。</span></span></p> \n </div> \n <p><br clear=\"all\"> &nbsp;</p> \n <p style=\"line-height: 28pt\"><span style=\"font-size: 16pt\"><span style=\"font-family: 方正黑体_gbk\">附件</span></span></p> \n <p align=\"center\" style=\"text-align: center; layout-grid-mode: char\"><span style=\"font-size: 18pt\"><span style=\"font-family: 方正小标宋简体\">2016</span></span><span style=\"font-size: 18pt\"><span style=\"font-family: 方正小标宋简体\">年第三批主城及周边区县大气污染源明细表</span></span></p> \n <p align=\"center\" style=\"text-align: center; layout-grid-mode: char\">&nbsp;</p> \n <table align=\"center\" border=\"1\" cellpadding=\"0\" cellspacing=\"0\" style=\"border-bottom: medium none; border-left: medium none; border-collapse: collapse; border-top: medium none; border-right: medium none\"> \n  <tbody> \n   <tr> \n    <td style=\"border-bottom: windowtext 1pt solid; border-left: windowtext 1pt solid; padding-bottom: 0cm; padding-left: 5.4pt; width: 52px; padding-right: 5.4pt; vertical-align: top; border-top: windowtext 1pt solid; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center; line-height: 20pt\"><span style=\"font-family: times new roman\"><span style=\"font-size: 14pt\"><span style=\"font-family: 方正黑体简体\">序号</span></span></span></p> </td> \n    <td style=\"border-bottom: windowtext 1pt solid; border-left: #f0f0f0; padding-bottom: 0cm; padding-left: 5.4pt; width: 340px; padding-right: 5.4pt; border-top: windowtext 1pt solid; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center; layout-grid-mode: char\"><span style=\"font-size: 14pt\"><span style=\"font-family: 方正黑体简体\"><span style=\"font-family: times new roman\">未达标的污染源名单</span></span></span></p> </td> \n    <td style=\"border-bottom: windowtext 1pt solid; border-left: #f0f0f0; padding-bottom: 0cm; padding-left: 5.4pt; width: 346px; padding-right: 5.4pt; border-top: windowtext 1pt solid; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center; layout-grid-mode: char\"><span style=\"font-size: 14pt\"><span style=\"font-family: 方正黑体简体\"><span style=\"font-family: times new roman\">主要污染状况</span></span></span></p> </td> \n    <td style=\"border-bottom: windowtext 1pt solid; border-left: #f0f0f0; padding-bottom: 0cm; padding-left: 5.4pt; width: 199px; padding-right: 5.4pt; border-top: windowtext 1pt solid; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center; layout-grid-mode: char\"><span style=\"font-size: 14pt\"><span style=\"font-family: 方正黑体简体\"><span style=\"font-family: times new roman\">责任单位</span></span></span></p> </td> \n   </tr> \n   <tr> \n    <td style=\"border-bottom: windowtext 1pt solid; border-left: windowtext 1pt solid; padding-bottom: 0cm; padding-left: