INSERT INTO `site_info` VALUES ('227', '<h1>\n <!--<$[TITLE]>start-->重庆市人民政府办公厅关于印发2015年大气污染防治重点工作目标任务分解的通知\n <!--<$[TITLE]>end--></h1><div id=\"Zoom\"> \n <!--<$[CONTENT]>start--> \n <p></p>\n <p style=\"text-align: center\"><span style=\"font-size: 16px\"><span style=\"color: black\"><span style=\"font-family: 宋体\">渝府办发〔2015〕49号</span></span></span></p> \n <p align=\"left\" style=\"text-align: left; line-height: 20.75pt; background: white\"><span style=\"font-size: 16px\"><span style=\"color: black\"><span style=\"font-family: 宋体\">各区县（自治县）人民政府，市政府有关部门，有关单位：</span></span></span></p> \n <p align=\"left\" style=\"text-align: left; line-height: 20.75pt; text-indent: 18pt; background: white\"><span style=\"font-size: 16px\"><span style=\"color: black\"><span style=\"font-family: 宋体\">为贯彻落实《大气污染防治行动计划》（国发〔2013〕37号）、环境保护部与我市签订的《重庆市大气污染防治目标责任书》、《中共重庆市委重庆市人民政府关于加快推进生态文明建设的意见》（渝委发〔2014〕19号）、《重庆市蓝天行动实施方案（2013―2017年）》（渝府发〔2013〕43号）和《重庆市人民政府关于贯彻落实大气污染防治行动计划的实施意见》（渝府发〔2013〕86号）要求，按期完成2015年全市大气污染防治目标任务，实现空气质量改善目标，经市政府同意，现将《2015年大气污染防治重点工作目标任务分解》印发给你们，请认真贯彻执行。</span></span></span></p> \n <p align=\"right\" style=\"text-align: right; line-height: 20.75pt; margin: 0cm 27.3pt 0pt 0cm; background: white\"><span style=\"font-size: 16px\"><span style=\"color: black\"><span style=\"font-family: 宋体\">重庆市人民政府办公厅</span></span></span></p> \n <p align=\"right\" style=\"text-align: right; line-height: 20.75pt; margin: 0cm 27.3pt 0pt 0cm; background: white\"><span style=\"font-size: 16px\"><span style=\"color: black\"><span style=\"font-family: 宋体\">2015年4月3日</span></span></span></p> \n <p align=\"left\" style=\"text-align: left; line-height: 20.75pt; text-indent: 24pt; background: white\"><span style=\"font-size: 12pt\"><span style=\"color: black\"><span style=\"font-family: 宋体\">（此件公开发布）</span></span></span></p> \n <p><br clear=\"all\"> &nbsp;</p> \n <p align=\"left\" style=\"text-align: left; line-height: 25pt; layout-grid-mode: char; background: white\">&nbsp;</p> \n <p align=\"center\" style=\"text-align: center; line-height: 25pt; layout-grid-mode: char; background: white\"><span style=\"font-size: 18pt\"><span style=\"color: black\"><span style=\"font-family: 宋体\">2015</span></span></span><span style=\"font-size: 18pt\"><span style=\"color: black\"><span style=\"font-family: 黑体\">年大气污染防治重点工作目标任务分解</span></span></span></p> \n <p align=\"left\" style=\"text-align: left; line-height: 25pt; layout-grid-mode: char; background: white\">&nbsp;</p> \n <table align=\"center\" border=\"1\" cellpadding=\"0\" cellspacing=\"0\" style=\"border-bottom: medium none; border-left: medium none; width: 649px; border-collapse: collapse; border-top: medium none; border-right: medium none\" width=\"649\"> \n  <thead> \n   <tr style=\"height: 28px\"> \n    <th style=\"border-bottom: windowtext 1pt solid; border-left: windowtext 1pt solid; padding-bottom: 0cm; padding-left: 0.85pt; width: 60px; padding-right: 0.85pt; height: 28px; border-top: windowtext 1pt solid; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center\"><span style=\"font-size: 9pt\"><span style=\"font-family: 黑体\">类别</span></span></p> </th> \n    <th style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 53px; padding-right: 0.85pt; height: 28px; border-top: windowtext 1pt solid; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center\"><span style=\"font-size: 9pt\"><span style=\"font-family: 黑体\">目标及</span></span></p> <p align=\"center\" style=\"text-align: center\"><span style=\"font-size: 9pt\"><span style=\"font-family: 黑体\">措施</span></span></p> </th> \n    <th style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 21px; padding-right: 0.85pt; height: 28px; border-top: windowtext 1pt solid; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center\"><span style=\"font-size: 9pt\"><span style=\"font-family: 黑体\">序号</span></span></p> </th> \n    <th style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 358px; padding-right: 0.85pt; height: 28px; border-top: windowtext 1pt solid; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center\"><span style=\"font-size: 9pt\"><span style=\"font-family: 黑体\">主</span></span>&nbsp;&nbsp;&nbsp; <span style=\"font-size: 9pt\"><span style=\"font-family: 黑体\">要</span></span>&nbsp;&nbsp;&nbsp; <span style=\"font-size: 9pt\"><span style=\"font-family: 黑体\">内</span></span>&nbsp;&nbsp;&nbsp; <span style=\"font-size: 9pt\"><span style=\"font-family: 黑体\">容</span></span></p> </th> \n    <th style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 69px; padding-right: 0.85pt; height: 28px; border-top: windowtext 1pt solid; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center\"><a name=\"_Toc354732029\"><span style=\"font-size: 9pt\"><span style=\"font-family: 黑体\">牵头单位</span></span></a></p> </th> \n    <th style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 88px; padding-right: 0.85pt; height: 28px; border-top: windowtext 1pt solid; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center\"><span style=\"font-size: 9pt\"><span style=\"font-family: 黑体\">责任单位</span></span></p> </th> \n   </tr> \n  </thead> \n  <tbody> \n   <tr style=\"height: 5px\"> \n    <td rowspan=\"2\" style=\"border-bottom: windowtext 1pt solid; border-left: windowtext 1pt solid; padding-bottom: 0cm; padding-left: 0.85pt; width: 60px; padding-right: 0.85pt; height: 5px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"left\" style=\"text-align: left; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">一、空气质量目标</span></span></p> </td> \n    <td nowrap style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 53px; padding-right: 0.85pt; height: 5px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"left\" style=\"text-align: left; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">主城区整体空气质量</span></span></p> </td> \n    <td style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 21px; padding-right: 0.85pt; height: 5px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">1</span></span></p> </td> \n    <td style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 358px; padding-right: 0.85pt; height: 5px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"left\" style=\"text-align: left; line-height: 14.3pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">①按照《环境空气质量标准》（</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">GB3095</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">―</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">2012</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">）评价，主城区空气质量优良天数保持在</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">240</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">天以上，重污染天气低于</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">18</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">天。</span></span></p> <p align=\"left\" style=\"text-align: left; margin: 0cm -24.55pt 0pt 0cm; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">②空气中</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">PM<sub>2.5</sub></span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">年均浓度比</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">2013</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">年下降</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">8%</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">以上，二氧化二氧化硫、二氧化氮年均浓度保持达标。</span></span></p> </td> \n    <td style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 69px; padding-right: 0.85pt; height: 5px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center; layout-grid-mode: char\"><span style=\"font-size: 12pt\"><span style=\"font-family: 方正书宋_gbk\">市环保局</span></span></p> </td> \n    <td style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 88px; padding-right: 0.85pt; height: 5px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"left\" style=\"text-align: left; layout-grid-mode: char\"><span style=\"letter-spacing: -0.1pt\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">主城各区人民政府（含两江新区、北部新区管委会，下同），市政府有关部门</span></span></span></p> </td> \n   </tr> \n   <tr style=\"height: 253px\"> \n    <td nowrap style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 53px; padding-right: 0.85pt; height: 253px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"left\" style=\"text-align: left; line-height: 14.3pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">各区县（自治县）空气质量</span></span></p> </td> \n    <td style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 21px; padding-right: 0.85pt; height: 253px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center; line-height: 14.3pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">2</span></span></p> </td> \n    <td style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 358px; padding-right: 0.85pt; height: 253px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"left\" style=\"text-align: left; line-height: 14.3pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">①按照《环境空气质量标准》（</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">GB3095</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">―</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">2012</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">）评价，主城各区空气质量优良天数均能保持在</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">240</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">天以上；重污染天气控制在较少水平，其中：渝中区低于</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">28</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">天、大渡口区低于</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">16</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">天，江北区低于</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">18</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">天，沙坪坝区低于</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">22</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">天、九龙坡区低于</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">28</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">天、南岸区低于</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">16</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">天、北碚区低于</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">16</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">天、渝北区低于</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">17</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">天、巴南区低于</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">16</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">天，北部新区低于</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">28</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">天；空气中</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">PM<sub>2.5</sub></span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">年均浓度比</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">2013</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">年下降</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">8%</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">以上。</span></span></p> <p align=\"left\" style=\"text-align: left; line-height: 14.3pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">②按照《环境空气质量标准》（</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">GB3095</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">―</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">1996</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">）评价，主城区以外区县（自治县）及万盛经开区城区空气质量优良天数达到</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">323</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">天以上且不低于</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">2014</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">年水平，空气中可吸入颗粒物、二氧化硫、二氧化氮年均浓度达标或较</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">2014</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">年下降。</span></span></p> </td> \n    <td style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 69px; padding-right: 0.85pt; height: 253px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center; line-height: 14.3pt; layout-grid-mode: char\"><span style=\"font-size: 12pt\"><span style=\"font-family: 方正书宋_gbk\">市环保局</span></span></p> </td> \n    <td style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 88px; padding-right: 0.85pt; height: 253px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"left\" style=\"text-align: left; line-height: 14.3pt; layout-grid-mode: char\"><span style=\"letter-spacing: -0.1pt\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">各区县（自治县）人民政府（含两江新区、北部新区、万盛经开区管委会，下同），市政府有关部门</span></span></span></p> </td> \n   </tr> \n   <tr style=\"height: 330px\"> \n    <td style=\"border-bottom: windowtext 1pt solid; border-left: windowtext 1pt solid; padding-bottom: 0cm; padding-left: 0.85pt; width: 60px; padding-right: 0.85pt; height: 330px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"left\" style=\"text-align: left; line-height: 14.3pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">二、控制扬尘污染</span></span></p> </td> \n    <td nowrap style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 53px; padding-right: 0.85pt; height: 330px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"left\" style=\"text-align: left; line-height: 14.3pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">控制施工扬尘</span></span></p> </td> \n    <td style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 21px; padding-right: 0.85pt; height: 330px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center; line-height: 14.3pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">3</span></span></p> </td> \n    <td style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 358px; padding-right: 0.85pt; height: 330px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"left\" style=\"text-align: left; line-height: 14.3pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">①督促施工工地建设单位编报控尘方案，并将控制扬尘经费纳入工程经费预算，严格按照《重庆市主城区尘污染防治办法》（重庆市人民政府令第</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">272</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">号）规定落实扬尘控制八项强制性规定，即设置密闭施工围挡、实行硬地坪施工、设置沉沙井及截水沟、开展湿法作业、车辆密闭运输、安装自动冲洗设备、建筑材料覆盖、配合环保局开展施工工地在线监测等设备安装工作；对控尘措施不到位、不达标的施工单位采取约谈、处罚等惩戒措施并纳入企业诚信体系建设，确保扬尘污染控制在施工场地之内。</span></span></p> <p align=\"left\" style=\"text-align: left; line-height: 14.3pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">②配合环保部门分别在主城各区至少</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">50</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">个施工工地安装扬尘在线监测设备。</span></span></p> <p align=\"left\" style=\"text-align: left; line-height: 14.3pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">③在各区县（自治县）城区建成并巩固扬尘控制示范工地</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">100</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">个。</span></span></p> <p align=\"left\" style=\"text-align: left; line-height: 14.3pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">④制定空气质量预警响应方案，落实预警应对措施。</span></span></p> <p align=\"left\" style=\"text-align: left; line-height: 14.3pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">⑤巩固施工扬尘控制“百日攻坚行动”的阶段性成果，并建立长效机制。</span></span></p> </td> \n    <td style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 69px; padding-right: 0.85pt; height: 330px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center; line-height: 14.3pt; layout-grid-mode: char\"><span style=\"font-size: 12pt\"><span style=\"font-family: 方正书宋_gbk\">市城乡建委</span></span></p> </td> \n    <td style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 88px; padding-right: 0.85pt; height: 330px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"left\" style=\"text-align: left; line-height: 14.3pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">各区县（自治县）人民政府，市交委、市国土房管局、市市政委、市水利局、市园林局等</span></span></p> </td> \n   </tr> \n   <tr style=\"height: 471px\"> \n    <td rowspan=\"3\" style=\"border-bottom: windowtext 1pt solid; border-left: windowtext 1pt solid; padding-bottom: 0cm; padding-left: 0.85pt; width: 60px; padding-right: 0.85pt; height: 471px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"left\" style=\"text-align: left; line-height: 16pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">二、控制扬尘污染</span></span></p> </td> \n    <td nowrap style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 53px; padding-right: 0.85pt; height: 471px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"left\" style=\"text-align: left; line-height: 16pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">控制道路扬尘</span></span></p> </td> \n    <td style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 21px; padding-right: 0.85pt; height: 471px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center; line-height: 16pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">4</span></span></p> </td> \n    <td style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 358px; padding-right: 0.85pt; height: 471px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"left\" style=\"text-align: left; line-height: 16pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">①严格落实道路清扫保洁“五项规定”，即：制定并落实清扫保洁规程、扩大机械化清扫作业比例、扩大道路清扫作业频次、保障道路冲洗和清扫作业机具、保障道路冲洗和清扫作业人员及经费。</span></span></p> <p align=\"left\" style=\"text-align: left; line-height: 16pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">②加大主城区城市建成区道路清扫保洁和冲洗频次，</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\"> 1</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">月、</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">2</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">月、</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">11</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">月、</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">12</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">月主、次干道每日至少冲洗</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">1</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">次、洒水</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">3</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">次，其他路段每日至少洒水</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">2</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">次；城市建成区道路机械化清扫率达到</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">86%</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">以上，满足国家考核要求。严格落实道路保洁质量要求，主、次干道路面及路边的绿化植被、路牌、栏杆等不得有明显积尘，确保显露本色。</span></span></p> <p align=\"left\" style=\"text-align: left; line-height: 16pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">③在各区县（自治县）城区建成并巩固扬尘控制示范道路</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">100</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">条。</span></span></p> <p align=\"left\" style=\"text-align: left; line-height: 16pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">④督促建筑渣土消纳场严格按照《重庆市主城区尘污染防治办法》（重庆市人民政府令第</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\"> 272 </span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">号）规定做好扬尘控制工作并达到有关标准；责令达不到控尘规范的建筑渣土消纳场停业整改。</span></span></p> <p align=\"left\" style=\"text-align: left; line-height: 16pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">⑤配合环保部门分别在主城各区至少</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 宋体\">10</span></span><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">条道路安装扬尘在线监测设备。</span></span></p> <p align=\"left\" style=\"text-align: left; line-height: 16pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">⑥制订空气质量预警响应方案，落实预警措施。</span></span></p> <p align=\"left\" style=\"text-align: left; line-height: 16pt; layout-grid-mode: char\"><span style=\"font-size: 9pt\"><span style=\"font-family: 方正书宋_gbk\">⑦巩固道路扬尘控制“百日攻坚行动”的阶段性成果，并建立长效机制。</span></span></p> </td> \n    <td style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 69px; padding-right: 0.85pt; height: 471px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"center\" style=\"text-align: center; line-height: 16pt; layout-grid-mode: char\"><span style=\"font-size: 12pt\"><span style=\"font-family: 方正书宋_gbk\">市市政委</span></span></p> </td> \n    <td style=\"border-bottom: windowtext 1pt solid; border-left: #000000; padding-bottom: 0cm; padding-left: 0.85pt; width: 88px; padding-right: 0.85pt; height: 471px; border-top: #000000; border-right: windowtext 1pt solid; padding-top: 0cm\"> <p align=\"left\" style=\"text-align: left; line-height: 16pt; layout-grid-mode: char\"><s